INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14395,1,305,'Dopo funzionamento prolungato Spesso troviamo inverter visti come \"delinquent\" che rimangono a potenza zero','Impianto fatto da 2 REACT2 su banco Bucci\r\nFirmware REACT2 = 19DB2 (A016-B018-C24D-H104-J005-Z031)\r\nSoftware Q1 = v 0.3.3 (REVO-EXPORTLIM-v0.3.3_3153.ben)\r\n\r\nMentre eseguivi i test di regolazione potenza, un inverter ha smesso di partecipare al controllo.\r\nEsattamente quello connesso in deasy chain e che non gestisce il Meter.\r\nRisulta in stato \"delinquent\" per l\'altro inverter.\r\n\r\nAllego screenshot presi con Postman.\r\n\r\nNota Bene: i due inverter sono regolarmente connessi alla rete LAN\r\nLa webUI e Postman lo confermano\r\nSe eseguo un reset generale (tramite rimuovendo AC e DC voltage) il sistema riparte.',NULL,943,5,195,5,NULL,195,4,'2019-02-08 17:30:50','2019-04-04 16:45:14','2019-02-08',0,NULL,14934,14934,2,3,0,'2019-03-01 11:54:25',NULL,6680);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14402,1,305,'Impianto 2 REACT2-3.6-TL-OUTD --> quando gli Inverters funzionano in modo sbilanciato durante CLC, è probabile oscillazione potenza dovuta e fall-back che entra e esce, ','Impianto fatto da 2 REACT2 su banco Bucci\r\nFirmware REACT2 = 19DB2 (A016-B018-C24D-H104-J005-Z031)\r\nSoftware Q1 = v 0.3.3 (REVO-EXPORTLIM-v0.3.3_3153.ben)\r\n\r\nPin disponibile su ciascun inverter 3000W (450V x 7.2A)\r\nCarico 2800W circa\r\nPVP = 6000W\r\nSet-point Export Limit = 0.5\r\nGuardband = 0.01\r\n\r\nSono passato da Set-point = 0 a Set-point = 0.5 (circa 3000W). Si è innescata una condizione di fall-back e di oscillazione. I due inverter non riuscivano a erogare una quantità di potenza adeguata\r\nHo notato che gli inverter lavoravano sbilanciati: uno andava al suo massimo (mppt) e l\'altro in limitazione ad un livello inferiore.\r\nAumentando leggermante la potenza degli inverter a un valore superiore a 3000W ciascuno, il sistema si è ripreso ed ha ricominciato a regolare con potenze bilanciate.\r\n\r\nAllego screen configurazione postman.\r\nNon ho registratoi i dati nella condizione di oscillazione (...sigh...)\r\n \r\n',NULL,943,4,195,5,NULL,195,7,'2019-02-08 18:34:55','2020-02-21 14:08:03','2019-02-08',0,NULL,14934,14934,4,5,0,NULL,NULL,6687);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14411,1,305,'Le POTENZE degli inverter dell\'impianto possono diventare SBILANCIATE (non distribuite uniformemente)','Impianto fatto da 2 REACT2 su banco Bucci\r\nFirmware REACT2 = 19DB2 (A016-B018-C24D-H104-J005-Z031)\r\nSoftware Q1 = v 0.3.3 bis (modificata da Faggiani il 10 Feb)\r\n\r\nPin disponibile su ciascun inverter 2250(450V x 5A)\r\nCarico 1500W circa\r\nPVP = 6000W\r\n*PAC_MAX e SAC_MAX inverter impostate a 3030.*\r\nSet-point Export Limit = 0.1\r\nGuardband = 0.01\r\n\r\nNon troppo di rado le potenze degli inverter non sono bilanciate.\r\nLo si vede anche dai set-point interni che non sono bilanciati.\r\nAllego i file JSON scaricati tramite Postman in cui si vede che:\r\n* Inverter 10:ce:a9:91:94:31 ha set-point=28% e lavora a potenza 850W\r\n* Inverter 44:ea:d8:a0:54:ba ha set-point= 40% e lavora a 1200W\r\n\r\nCambiando carico lo sbilanciamento persiste.\r\nIn seguito ho fatto ON-OFF DC a AC source ed il problema è sparito: i tentativi successivi hanno dato esito negativo nel senso che la potenza era bilanciata.\r\n\r\n',NULL,943,2,NULL,4,NULL,195,9,'2019-02-11 15:06:35','2019-05-31 14:26:57','2019-02-11',0,NULL,14935,14935,2,3,0,NULL,NULL,6696);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14434,1,305,'Impianto 2 REACT2-3.6-TL-OUTD --> Le potenze dei 2 inverter sono state a lungo a zero con LOAD=0 e Set-point=840W','Impianto fatto da 2 REACT2-3.6-Tl-OUTD su banco Bucci\r\nFirmware REACT2 = 19DB2 (A016-B018-C24D-H104-J005-Z031)\r\nSoftware Q1 = v 0.3.3 bis revisionato 10 feb\r\n\r\nPin disponibile su ciascun inverter 3000W (450V x 7.2A)\r\nCarico cambiato da 2000W a 0W \r\nPVP = 6000W\r\nSet-point Export Limit = 0.15\r\nGuardband = 0.01\r\n\r\nCon carico = 2000W e set-point=840W (=(0.15-0.1)*6000) i due inverter stavano erogando potenza complessiva di circa 2840W in modo bilanciato.\r\nHo fatto delle transizioni di carico fino a toglierlo completamente (load = 0W)\r\nMi aspettavo che i due inverter erogassero una potenza complessiva di 840W. Invece sono stati a lungo (qualche minuto) ad erogare una potenza = 0W.\r\nI parametri scaricati con POSTMAN confermano che i due inverter non erano \"delinquent\", non erano in fall-back ma avevano i set-point entrambi a zero.\r\n\r\nDopo qualche minuto la potenza corretta ha fatto qualche oscillazione ed è stata ripristinata.\r\n\r\nAllego i file JSON scaricati con POSTMAN.\r\n',NULL,943,4,195,4,NULL,195,6,'2019-02-12 11:45:59','2020-12-21 16:03:51','2019-02-12',0,NULL,14934,14934,6,7,0,NULL,NULL,6719);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14455,1,305,'Impianto 2 REACT2-3.6-TL-OUTD --> Dopo programmazione FW inverter, uno dei due era in \"delinquency\"','Impianto fatto da 2 REACT2-3.6-TL-OUTD su banco Bucci, Software Q1 = v 0.3.3 ter revisionato 12 feb.\r\n\r\nHo lanciato una programmazione dell\'inverter tramite scheda wi-fi. \r\nFW REACT2 di partenza *19DB2* (A016-B018-C24D-H104-J005-Z031), FW di arrivo *19DB3* (A016-B018-C24D-H104-J005-Z033) con nuova versione di Flash. \r\n\r\nHo lanciato una programmazione per volta aspettando che finisse. Durante la programmazione il sistema eroga una potenza che non sempre è in linea con il target del CLC-PEL.\r\nAl termine della programmazione di entrambi gli inverter ho fatto un software reset per sicurezza e li ho fatti riconnettere.\r\n\r\nAl termine della programmazione un inverter si trova in stato \"delinquent\" e sta erogando la potenza disponibile all\'ingresso. L\'altro vede che non riesce a abbassare la potenza al di sotto del target e quindi è in stato di fallback.\r\n\r\nAllego i files JSON scaricati con postman in questa condizioni.\r\n\r\n+ATTENZIONE:\r\ni dati scaricati con postman potrebbero essere riferiti a condizioni di LOAD e PIN disponibile differenti.\r\nNon sono in grado di ricostruitre: pertanto ometto questa informazione+\r\n\r\n_La notte ho sicuramente lasciato il sistema in queste conddizioni:\r\nPin disponibile su ciascun inverter 900W(450V x 2A), Carico di 500W, PVP = 6000W, Set-point Export Limit = 0.15, Guardband = 0.01._\r\n',NULL,943,4,195,4,NULL,195,5,'2019-02-13 10:32:00','2020-02-21 14:11:12','2019-02-13',100,NULL,14934,14934,8,9,0,NULL,NULL,6740);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14525,1,305,'Impianto 2 REACT2-3.6-TL-OUTD --> Oscillazioni anomale indesiderate durante funzionamento prolungato','Impianto 2 REACT2-3.6-TL-OUTD banco Bucci, Software Q1 = v 0.3.3 ter revisionato 14 feb.\r\nFW REACT2 19DB3 (A016-B018-C24D-H104-J005-Z033).\r\n\r\nPin disponibile su ciascun inverter 6000W (435VV x 1.5A)\r\nCarico 400W circa\r\nPVP = 6000W\r\nPAC_MAX e SAC_MAX inverter impostate a 3030.\r\nSet-point Export Limit = 0.06\r\nGuardband = 0.01\r\n\r\nHo lasciato il sistema nelle condizioni suddette da Venerdì 15 sera a Lunedì 18 mattina è ho scaricato i dati per 210.000 sec (1 sample/5sec).\r\nL\'analisi dei dati mostra 6 momenti in cui la potenza oscilla in modo indesiderato, ciascuna con durata massima di 1.0-1,5 ore.\r\nAnche i dati provenienti dalle API mostrano anomalie.\r\n\r\nHo allegato delle immagini espicative.\r\nIl file dei dati è molto grande: potete trovarlo al link X:\\BucciT\\REACT2-revolution\\Issue\\20190218\r\n\r\n\r\n\r\n',NULL,943,5,NULL,5,NULL,195,22,'2019-02-18 16:14:11','2019-04-04 16:52:05','2019-02-18',0,NULL,14934,14934,10,11,0,'2019-03-06 15:01:23',NULL,6807);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14542,1,305,'Impianto 2 REACT2-3.6-TL-OUTD -->Pexp non si stabilizza ma oscilla sotto al target (condizioni Zero power + Pinv_disp appena < P_Load) ','Impianto 2 REACT2-3.6-TL-OUTD banco Bucci, SoftwareQ1 = v0.3.4 18-Feb (elc 0.3.4, regulator version 4), FW REACT2 19DB3 (A016-B018-C24D-H104-J005-Z033).\r\n\r\nPin disponibile su ciascun inverter 3200W:\r\nRegatron curva 500Voc-435Vmmp-20AIsc-19AImpp impostato con Isc=7.74A.\r\n2_Xantrex Vdc=450V Idc=3.6Ax2\r\n*Carico 6100W* circa, *PVP = 6000W*, PAC_MAX e SAC_MAX inverter impostate a 3030.\r\nSet-point Export Limit = 0 Guardband = 0.01\r\n\r\nDurante la prova di Step responce ho notato che in condizioni di *Zero power* + *Pinv_disp appena < P_Load* con il carico inserito la Pexp è oscillante e non arriva al target.\r\nI set-point degli inverter non rimangono fissi a 1 come ci si potrebbe aspettare.\r\n\r\nAllego file esplicativi\r\n\r\n\r\n',NULL,943,4,195,4,NULL,195,10,'2019-02-19 17:11:34','2020-02-21 14:13:58','2019-02-19',50,NULL,14934,14934,12,13,0,NULL,NULL,6824);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14545,1,305,'Impianto 2 REACT2-3.6-TL-OUTD --> Eseguendo Load Step Responce più volte la risposta dinamica spesso è differente da caso a caso','Impianto 2 REACT2-3.6-TL-OUTD banco Bucci, SoftwareQ1 = v0.3.3 14-Feb, FW REACT2 19DB3 (A016-B018-C24D-H104-J005-Z033).\r\nPin disponibile su ciascun inverter 3200W:\r\nRegatron curva 500Voc-435Vmmp-20AIsc-19AImpp impostato con Isc=7.74A.\r\n2_Xantrex Vdc=450V Idc=3.6Ax2\r\nCarico 6000W circa, PVP = 6000W, PAC_MAX e SAC_MAX inverter impostate a 3030.\r\nSet-point Export Limit = *0.5* Guardband = *0.01*\r\n\r\nHo eseguito il primo test applicando il carico per 60sec, rmuovendolo per 240sec, applicandolo di nuovo per 60sec.\r\nHo eseguito il secondo test applicando e rimuovendo il carico ogni 20secondi per 300secondi.\r\n\r\nNel secondo test si nota che la risposta dinamica è differente da caso a caso e anche diversa da quella del primo test.\r\nIl sistema va in fallback ed in un caso si generea una doppia oscillazione al di sopra di Pexp_setpoint che ha un tempo di assestamento di quasi 15 sec e rischia di diventare un FAIL.\r\n\r\nAllego file esplicativi.\r\n\r\n ',NULL,943,4,195,4,NULL,195,7,'2019-02-19 17:49:37','2020-02-21 14:15:44','2019-02-19',0,NULL,14934,14934,14,15,0,NULL,NULL,6827);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14580,1,305,'Prova Vita PVS-50 --> Oscillazioni anomale indesiderate durante test di lunga durata','Impianto Prova Vita Mizar con 8 PVS-50 connessi\r\nFW_inv = 1844B + inverterBF3B\r\nSW_Q1 = SW_1.3.2_elc_0.3.6_reg_5\r\nPin disponibile su ciascun inverter > 60KW (rettificatore)\r\nCarico 42KW x 6 = 252KW circa\r\nPVP = 440KW\r\nSet-point Export Limit = 0.15\r\nGuardband = 0.01\r\n\r\nHo lasciato il sistema nelle condizioni suddette in modo permanetne dalle 18:00 21Feb alle 9:00 22Feb acquisendo per 55000 sec   (1 dato ogni 2sec)\r\nL\'analisi dei dati mostra 2 momenti in cui la potenza oscilla in modo indesiderato, con durata 1.0-1,5 ore.\r\nAnche i dati provenienti dalle API mostrano anomalie.\r\nAllego immagini esplicative\r\nIl ticket è correlato al ticket http://monitoring-helpdesk.it.abb.com/issues/14525\r\n\r\n',NULL,943,5,NULL,6,NULL,195,13,'2019-02-22 12:05:36','2019-04-04 17:01:33','2019-02-22',0,NULL,14934,14934,16,17,0,'2019-03-08 12:10:14',NULL,6861);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14589,2,305,'Prova Vita PVS-50 --> Estrema difficoltà ad eseguire test DVT tramite ATP + API usate in modo continuativo','Impianto Prova Vita Mizar con 8 PVS-50 connessi\r\nFW_inv = 1901B\r\nSW_Q1 = SW_1.3.2_elc_0.3.6.1_reg_5\r\nPin disponibile su ciascun inverter > 60KW (rettificatore)\r\nCarico simulato fino a 40KW x 6 = 240KW circa\r\nPVP = 440KW\r\nSet-point Export Limit = 0\r\nGuardband = 0.01\r\n\r\nI test DVT vengono eseguiti tramite piattaforma ATP che scarica i dati dal WT500 tramite comandi LAN e dagli inverter tramite API.\r\nIl test DVT consiste nell\'impostare un punto di lavoro e monitorare il sistema per circa 180sec scaricando dati a 0.3sec.\r\nDopo alcune sessioni di test DVT andati a buon fine comincio a sperimentare problemi da parte del programma ATP. Dal Log vengono segnalati problemi legati all\'uso delle API.\r\n\r\nSegue un lungo periodo di tempo (decine di minuti) in cui l\'impianto di fatto non funziona più a dovere a causa di problemi di comunicazione interna sul bus LAN del Q1+Inverter.\r\nHo provato a lanciare la API con Postman con i seguenti risultati:\r\n* L\'inverter che gestisce il meter 10.21.37.62 non risponde alle API.\r\n* L\'inverter 10.21.37.68 risponde e vede il meter come delinquent\r\n* L\'inverter 10.21.37.113 risponde e vede il 10.21.37.62 come delinquet.\r\n* non ho provato sugli altri.\r\n\r\nA volte l\'impianto torna a regolare: allora ho rilanciato il test ma il problema si è riproposto.\r\nAspettando, alla fine il sistema torna a funzionare come nulla fosse: rilanciando il test DVT questa volta va a buon fine.\r\n\r\n*Credo ci sia un legame fra l\'impiego delle API fatto da ATP e questi blocchi / malfunzionamneti.*\r\n\r\n*+Vi prego di aiutarmi a risolvere il problema con la massima urgenza perchè non sono in grado di eseguire i test automatici DVT nel modo sperato e programmato.+*\r\n\r\nAllego file salvati durante il fallimento di 2 test DVT.\r\n \r\n\r\n\r\n\r\n\r\n\r\n\r\n',NULL,943,5,195,7,NULL,195,6,'2019-02-23 15:38:34','2019-04-04 17:00:04','2019-02-23',0,NULL,14934,14934,18,19,0,'2019-03-01 11:44:39',NULL,6870);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14725,1,305,'Prova vita PVS-50 --> Programmando SW Q1 che gestisce il METER, è possibile superareil target di PEL per più di 15 sec --> FAIL','Impostato controllo su METER B24\r\nSW Q1 v0.1.3.4, \"elcFirmwareVersion\": \"0.3.7.1\", \"regulatorVersion\": \"5\".\r\n• Potenza nominale impianto = 440KW\r\n• Potenza effettivamente disponibile causa derating vs Vin = 330KW.\r\n• Impostato set-point PEL a 0% con gb 1% (-4400W)\r\n• Impostato carico a 3% (9.9KW).\r\n• Cambiati i parametri di fallback in accordo a requisiti AUSTRALIA:\r\n\"fallback\": {\r\n\"enable\": true,\r\n\"percentage\": 0,\r\n\"deadline\": 14000 (msec),\r\n\"guardTimeS\": 60 (sec).\r\n\r\nCon impianto in funzione lancio aggiornamento software Q1 *\"0a:de\"* con IP 10.21.37.68 *che gestisce Meter B24*\r\nIl SW di UPLOAD è \"EXP_LIM-TRIOTM-Q135-v1.3.3_3203.ben\"\r\n\r\nRisultati: \r\nquando il Q1 viene riavviato in seguito al sw upload, il meter B24 \"sparisce\" dalla comunicazione e tutti gli altri lo vedono come delinquent.\r\nDopo 14000msec tutti gli altri applicano la fallback e vanno a potenza zero.\r\nL\'inverter con il Q1 *\"0a:de\"* invece continua ad eseguire l\'ultimo set-point fino al timeout di 1 minuto e poi ripristina il livello pout = 100%.\r\nIn questa fase la P_EXPORTED va oltre il target per tutto il tempo necessario a completare il riavvio del Q1. \r\nQuando il riavvio è completo la P_EXPORTED viene abbassata sotto il set-point.\r\n*Purtroppo il tempo è maggiore di 15sec e questa è considerabile come un FAIL*\r\n\r\nAbbiamo misurato un tempo di circa 90 secondi per il riavvio completo del Q1.\r\nIl problema si risolve modificando il comando di power reduction temporaneo usato dal PEL-CLC: *è necessario aumentare il timeout da 0 (=1min) a 2 min*\r\nIn questo modo, per tutto il riavvio, l\'inverter continua ad eseguire il comando precedente senza rischiare di superare il set-point di PEL-CLC.\r\n\r\nOvviamente questa è una soluzione solo per la prova di tipo statico. \r\n*Se si volesse considerare e risolvere anche il caso dinamico (variazione di LOAD durante il riavvio del Q1) sarebbe necessario che, prima del riavvio, il Q1 desse all\'inverter il set-point 0.*\r\n\r\nAllego file esplicativi\r\n',NULL,943,4,195,4,NULL,195,9,'2019-03-11 18:15:57','2020-02-21 14:27:45','2019-03-11',100,NULL,14935,14935,4,5,0,NULL,NULL,7003);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14842,1,305,'Comunicazione con Meter M2M, B23 e B24 bloccata dopo evento mancanza di rete AC prolungata','Impianto 2 REACT2-3.6-TL-OUTD banco Bucci, SoftwareQ1 = *v0.5.1*, FW REACT2 *1911D* (A021-B018-C253-H108-J005-Z035).\r\nPin disponibile su ciascun inverter 400W e 800W:\r\nRegatron senza curva 400Vdc-2A-800W\r\nITECH IT6018C-1500-30 con curva con 400Vmmp e 400W.\r\nCarico 1200W circa, PVP = 6000W, PAC_MAX e SAC_MAX inverter impostate a 3030.\r\nSet-point Export Limit = 0.01 Guardband = 0.01\r\nMeter M2M Ethernet\r\n\r\nI due inverter erano stati lasciati in prova la notte: si è verificato una interruzione della corrente che ha spento le attrezzature (non so stabilire per quanto).\r\n\r\nAbbiamo trovato i due REACT2 alimentati lato AC ma disalimentati lato DC.\r\nL\'ausiliaria interna viene comunque generata anche dalla sola AC e quindi la logica dell\'inverter, il Q1 e il METER M2M erano alimentati.\r\nApplicando la tensione DC gli inverter si sono connessi in rete ma il set-point rimane fisso a 0%.\r\nSono in uno stato di FALL-BACK in quanto il METER M2M non viene rilevato.\r\n\r\nAlessandro ha trovato la motivazione: il sistema si è avviato e ci sono stati almeno 1000 messaggi di errore nella comunicazione con il Meter.\r\nIl Software del Q1 che gestisce la comunicazione con METER, dopo 1000 messaggi errore, sospende la comunicazione (viene chiusa).\r\nQuesta serie di eventi lascia di fatto l\'impianto in condizione di fall-back permanente.\r\n\r\nAllego files esplciativi\r\n \r\n',NULL,943,5,195,7,NULL,195,14,'2019-03-26 13:02:06','2019-05-10 12:55:12','2019-03-26',80,NULL,14934,14934,20,21,0,'2019-05-10 12:55:12',NULL,7114);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14934,4,305,'TICKET DA RISOLVERE x PRIMO RILASCIO EXPORT LIMITATION','In questo container sono elencati tutti i ticket che devono essere risolti per il primo rilascio di Export limitation distribuito gestito da Q1',NULL,942,1,NULL,7,NULL,195,22,'2019-04-04 16:09:02','2019-09-16 10:03:34','2019-02-08',58,NULL,NULL,14934,1,28,0,NULL,NULL,7202);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14935,4,305,'TICKET DA RISOLVERE x SECONDO RILASCIO EXPORT LIMITATION','In questo container sono elencati tutti i ticket che devono essere risolti per il secondo rilascio di Export limitation distribuito gestito da Q1',NULL,942,1,NULL,6,NULL,195,19,'2019-04-04 16:49:03','2020-02-21 15:02:33','2019-02-11',17,NULL,NULL,14935,1,50,0,NULL,NULL,7203);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14980,1,305,'Prova vita PVS-50 --> Q1 bloccato in modo permanente dopo Reboot. Necessaria sostituzione.','Stavo aggiornando il SW Q1 delle macchine PVS-50-TL in prova vita.\r\nFW inverter = 1901B\r\nSW Q1 stavo passsando da v0.16.0 a v0.16.1\r\n\r\nSono partito da una situazione in cui il Q1 del n.7 comunicava correttamente all’indirizzo ip 10.21.37.81\r\nHo tentato di programmare in locale il Q1 da 0.16.0 a 0.16.1 ma non riconosce il file che gli passo in locale come valido.\r\nNon mi propone cioè nessun aggiornamento.\r\n\r\nD’accordo con Alessandro ho eseguito un REBOOT del Q1 tramite WEB-UI (Connectivity Reboot)\r\n*Dopo il reboot abbiamo perso completamente la possibilità di dialogare su LAN con il Q1.*\r\nHo perso la comunicazione anche con i Mizar n.8, 9 e 10 essendo in daisy chain LAN dopo il n.7.\r\n\r\nHo eseguito allora un ON-OFF completo dell’inverter tramite DC voltage OFF e ON.\r\nHo atteso parecchi minuti e che l’inverter tornasse a funzionare esportando potenza.\r\n*Non è però stato possibile ripristinare la connessione con il Q1 del n.7.\r\nLa scheda sembra non funzionante.\r\n*\r\n',NULL,943,2,18,6,NULL,195,5,'2019-04-09 12:42:41','2019-09-12 18:32:50','2019-04-09',0,NULL,14935,14935,6,7,0,NULL,NULL,7248);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15026,1,305,'PVS-100-TL-400 Lalli --> Dopo \"Restore to Default\" ci sono problemi a configurare la RS485 del Q1 per il METER (es. B23 o B24)','+Descrizione EUT+\r\nPVS-100-TL-400 campione Lalli\r\nSW-Q1  = *v0.14.2*\r\nFW-INV = *1909B*\r\n\r\nIl bottone \"Restore to default\" resetta il Q1 compreso le impostazioni di EXPORT LIMITATION. Dopo il reboot di Q1, il sistema risulta non configurato per le operazioni PEL e la configurazione deve essere eseguita nuovamente.\r\n*Il file Modbus_Launcher.json non è presente*--> viene creato con la configurazione di RS485.\r\nIn caso si stia utilizzando un meter RTU (RS485) connesso su un Logger, deve essere anche eseguita nuovamente la configurazione della RS485. Senza questa operazione non è possibile far comunicare il Meter con il Q1.\r\nNel caso del campione PVS-100-TL Lalli si sta usando un ABB B23 e la RS485 Logger deve essre configurata come ADD=1, BAUD=19200, PARITY=EVEN, PROT=DEVICE ACQUISITION.\r\n\r\nFacendo la configurazione da WEB-UI, quando si entra nel menu CONNECTIVITY --> RS485, ci viene proposta una configurazione già corretta, probabilmente \"ripescata\" dalla cache del browser.\r\nIl tasto \"Save\" non può essere cliccato. Basta cambiare il baud rate a 9600 e poi a 19200 per sbloccare il tasto \"Save\".\r\nCon tutti i parametri corretti adesso è possibile fare il \"Save\" --> l\'operazione viene eseguita e sembra andare a buon fine (confermata da un messaggio).\r\n\r\n*In realtà la RS485 non viene configurata ed il file \"Modbus_Launcher.json\" non viene creato. Il Meter non risulta accessibile.*\r\n\r\nL\'unico modo di far creare il file è quello di cambiare il protocollo da Device Aquisition a Modbus SunSpec Server e salvare.\r\nSuccessivamente ricambiare di nuovo il protocollo da Modbus Sunspec Server a Device Acquisition e salvare nuovamente.\r\n\r\nQuando si preme il tasto Save, la configurazione deve essere salvata sempre, senza nessuna eccezione.\r\n\r\n# L\'eccezione che ho individuato deve essere risolta.\r\n# E\' necessario verificare che non ci siano altre eccezioni di questo tipo.\r\n\r\nDa verificare se lo stesso problema si presenta con la INSTALLER APP.\r\nSe SI, la priorità del problema deve essere alzata.\r\n\r\nMetto il ticket in carico a Alessandro, che poi lo indirizzerà verso la risorsa più appropriata\r\n\r\n ',NULL,943,4,195,4,NULL,195,4,'2019-04-15 14:25:50','2019-06-10 18:04:02','2019-04-15',0,NULL,14935,14935,8,9,0,NULL,NULL,7294);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15027,2,305,'La regola di EREDITARE la configurazione PEL-CLC PIU\' RECENTE è PERICOLOSA','SW-Q1 v0.14.2 per PVS-100-TL Sirius\r\nSW-Q1 v1.6.2 per PVS-50-TL Mizar\r\n\r\nIl Marketing da quanto ho capito ha richiesto che, nel caso che un inverter venga inserito successivamente in un impianto dove è già stata eseguita una configurazione di EXPORT LIMITATION, questo inverter erediti *in modo automatico* la configurazione dell\'impianto in modo che la sua configurazione per diventare un inverter partecipante sia agevolata.\r\n\r\nQuesta richiesta è stata implementata nel seguente modo: tutti gli inverter partecipanti ad un impianto, nel caso ci siano fra essi configurazioni di EXPORT LIMITATION differenti, si allineano automaticamente alla configurazione *PIU\' RECENTE*. Questa strategia ha alla base l\'assunzione che, una volta configurato bene l\'impianto, nel caso venga aggiunto un ulteriore inverter questo sia stato resettato al default (configurazione assente) oppure abbia una configurazione più vecchia.\r\n\r\n*A mio avviso questa assunzione è pericolosa e questa strategia potrebbe causare problemi di \"sconfigurazione\" di impianti già precedentemente configurati e messi in servizio*.\r\n\r\nCaso D\'uso\r\nSe per qualunque ragione su un impianto viene installato un nuovo inverter e collegato alla rete LAN degli altri e in questo inverter esiste già una configurazione con data (time stamp) successiva a quella dell\'impianto, appena l\'inverter si accende ed il Q1 si connette alla rete LAN, la sua configurazione viene automaticamente copiata su tutti gli altri inverter. La conseguenza è l\'impianto viene sconfigurato e tutte le precedenti impostazioni fatta dell\'installatore vengono annullate. Tutto ciò prima di rendersi conto di ciò che sta avvenendo.\r\n\r\n+Credo che il problema debba essere discusso e affrontato.+\r\n\r\n   \r\n\r\n',NULL,943,1,NULL,5,NULL,195,3,'2019-04-15 14:49:47','2019-04-19 17:21:19','2019-04-15',0,NULL,14935,14935,10,11,0,NULL,NULL,7295);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15084,1,305,'Energia esportata e Energia assorbita: problemi nei dati raccolti dai Meter e inviati a portale','MIZAR FW=1901B e SW-Q1=v1.6.2\r\nSIRIUS A FW=1910B e SW-Q1=v0.14.2\r\nREACT2 FW=1912B e SW-Q1=0.5.4\r\n\r\nCi sono due problemi nella raccolta dati dai meter relativi a Energia Generata ed Energia Assorbita.\r\n\r\n*Problema 1*\r\nIl Q1 scarica dal meter M2M solo la potenza e non gli altri dati da inviare a portale.\r\nIl meter B23/B24 invece è implementato.\r\n\r\n*Problema 2*\r\nI meter sono utilizzati in genere per misurare le potenza assorbite dai carichi, dalle utenze. \r\nLa normale inserzione del Meter prevede che la potenza misurata sia POSITIVA quando è ASSORBITA dal carico e NEGATIVA invece quando va in senso opposto (generata).\r\nPer questi motivi, nei meter ABB M2M e ABB B23/B24, si trovano in uso le seguenti convenzioni (convenzione del carico):\r\n* POTENZA ASSORBITA è associata alla POTENZA DI SEGNO POSITIVO. Il contatore ENERGIA ASSORBITA viene incrementato quando la POTENZA misurata è POSITIVA.\r\n* POTENZA GENERATA è associata alla POTENZA DI SEGNO NEGATIVO. Il contatore ENERGIA GENERATA viene incrementato quando la POTENZA misurata è NEGATIVA.\r\n\r\nAttualmente il firmware del Q1 (vedi versioni software riportate all\'inizio) utilizza invece la convenzione del generatore:\r\n* Attribuisce a Potenza POSITIVA il significato di potenza ESPORTATA.\r\n* Attribuisce a Potenza NEGATIVA il significato di potenza ASSORBITA.\r\n* Il controllo si basa su queste assunzioni. Nel caso che il METER sia cablato con segno opposto, la installer APP permette di comunicarlo ai Q1 per prendere le giuste precauzioni.\r\n\r\nIl problema attuale del firmware del Q1 è che per quanto riguarda i dati inviati al portale, usa (per errore) la seguente rigida associazione:\r\n* L\'energia del METER con etichetta ENERGIA ASSORBITA viene considerata come quella ASSORBITA dalla rete verso il carico.\r\n* L\'energia del METER con etichetta ENERGIA GENERATA viene considerata come quella PRODOTTA dall\'impianto verso la rete.\r\n\r\nQuesto produce i due scenari riportati di seguito.\r\n\r\n*Scenario 1 --> Meter Cablato in accordo a EXPORT LIMITATION, cioè misura la potenza come POSITIVA quando è prodotta dall\'impianto ed ergoata in rete*\r\nPRO\r\nla potenza ha il segno corretto per il controllo di EXPORT LIMITATION\r\nCONTRO\r\nil contatore del METER con etichetta ENERGIA ASSORBITA rappresenta in realtà l\'ENERGIA GENERATA. \r\nil contatore del METER con etichetta ENERGIA GENERATA rappresenta in realtà l\'ENERGIA ASSORBITA.\r\n*I dati inviati a portale non sono coerenti*\r\n \r\n*Scenario 2 --> Meter Cablato in accordo al suo MANUALE (convenzione del carico), cioè misura la potenza come POSITIVA quando è assorbita dal CARICO, cioè va dalla rete verso l\'impianto.*\r\nPRO\r\nil contatore del METER con etichetta ENERGIA ASSORBITA rappresenta effettivamente l\'ENERGIA ASSORBITA. \r\nil contatore del METER con etichetta ENERGIA GENERATA rappresenta effettivamente l\'ENERGIA GENERATA.\r\nI dati inviati a portale sono coerenti.\r\nCONTRO\r\nla potenza ha il segno opposto a quello voluto dal controllo di EXPORT LIMITATION\r\nPer risolvere basta agire su una opzione disponibile su INSTALLER APP.\r\n\r\nCONCLUSIONI\r\nPer la WAVE 1.0 deve essere indicato nella procedura di utilizzo del PEL-CLC di inserire il meter in accordo allo scenario 2 perchè almeno in questo modo i dati sono coerenti.\r\nPer la WAVE 2.0 è necessario che il software del Q1 gestisca le energia prelevate dal meter ed inviate al portale in funzione dell\'inserzione e del Meter per avere dati coerenti.\r\n',NULL,943,3,195,7,NULL,195,5,'2019-04-19 19:08:53','2020-09-11 10:52:09','2019-04-19',0,NULL,14934,14934,22,23,0,NULL,NULL,7352);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15216,2,305,'Dopo ON-OFF ausiliarie al riavvio si può superare il set-point PEL e si verifica sempre un FALL-BACK di 60sec da mancata comunicazione con Meter','Descrizione EUT\r\nPVS-100-TL-400 campione Lalli\r\nSW-Q1 = v0.14.6\r\nFW-INV = 1911B\r\n\r\nCarico fisso a circa 4KW\r\nPotenza IN disponibile circa 10KW\r\nSet-point = 0%\r\n\r\nAbbiamo eseguito manovre DC ON-OFF-ON e monitorato Export limitation nella successiva connessione.\r\nIn questo esperimento le tensioni ausiliarie dell\'INVERTER e del Q1 vengono completamente spente in seguito alla manovra DC ON-OFF.\r\nDopo la manovra DC OFF-ON sia l\'INVERTER che Q1 fanno il REBOOT.\r\nIn particolare il Q1 si accende e poi fa il download dei dati dall\'inverter, manovra che dura circa 2 minuti. In questa fase export limitation non è attivo.\r\n*+Problema 1+*: l\'inverter procede con il suo avvio e riesce a connettersi in rete prima che Q1 abbia terminato. In questa fase il set-point di default \"forever\" dell\'inverter è 100% e quindi può essere erogata in rete una potenza fino al 100% della Pmax. Se c\'è potenza sufficiente è altamente probabile che il set-point di PEL venga superato.\r\nA questo punto anche eport limitation viene avviata.\r\n*+Problema 2+*: il codice che gestisce la comunicazione con il meter però sembra partire in ritardo rispetto a quello di export limitation. Export limitation trova il meter in comunication error e quindi si va in fallback per 60sec. Questo fallback potrebbe essere evitato e si potrebbe subito cercare di regolare la potenza.\r\n\r\nPer risolvere il problema 1 si potrebbe mettere in atto la strategia di impostare a zero il comando forever se è attivo export limitation e 100% se non è attivo.\r\nPer risolvere il problema 2 e cominciare a regolare fin da subito si potrebbe investigare se è possibile avviare il codice che gestisce il meter prima di export limitation.\r\n\r\nAllego Misure prese durante il test ed i file di configurazione.',NULL,943,4,195,5,NULL,195,8,'2019-05-10 16:31:30','2019-10-07 19:41:32','2019-05-10',100,NULL,14935,14935,12,13,0,NULL,NULL,7482);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15218,1,305,'PVS-100-TL-400: mancato caricamento configurazione Export Limitation all\'avvio (alba) giornaliero','Problema rilevato in impianto di prova Francia così configurato:\r\nn°3 PVS-100-TL-400\r\nSW-Q1 = v0.14.2\r\nFW-INV = 1911B\r\n\r\nDurante il riavvio mattutino quando sia Q1 che Inverter vengono avviati, la configurazione di export limitation non viene caricata.\r\nL\'impianto quindi va a lavorare con set-point non controllato, come se export limitation fosse disattivata.\r\nAbbiamo verificato che il contenuto dei file di configurazione PEL CLC è corretta ma non è stato caricata e attivata.\r\nNon è stato applicato dopo il reboot del Q1.\r\n\r\nLo stesso problema si è verificato anche semplicemente facendo il solo reboot del Q1 (prova fatta da Luigi in ssh).\r\nIl test è stato eseguito 2 volte: in 1 caso la configurazione non è stata caricata, in un altro invece si.\r\n\r\nIl problema è stato individuato: la configurazione non viene caricata quando il timestamp del file di configurazione viene riconosciuto come troopo avanti nel futuro.\r\nQuesto accade se, quando viene caricato il file di configurazione, il timestamp del Q1 non è ancora stato allineato a quello della rete internet e pertanto è al default (anno 1970!!!)\r\n\r\nNelle versioni Q1 MIZAR v.1.6.6 e Q1 SIRIUS v.0.14.6 il problema è stato risolto.\r\nLe versioni sono attualmente in prova.',NULL,943,4,195,7,NULL,195,3,'2019-05-10 16:43:04','2019-10-07 19:43:41','2019-05-10',0,NULL,14934,14934,24,25,0,'2019-05-20 19:50:28',NULL,7484);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15263,2,305,'Andamento \"caotico\" dei set-point Pow reduction degli inverter durante prove di cambiamento progressivo valore set-point PEL','Prova DVT eseguita su impianto prova vita cos\' configurato:\r\nImpianto prova vita\r\n7 PVS-50-TL configurati per CLC\r\n1 PVS-100-TL configurato per CLC\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nIn questa prova viene cambiato il set-point di PEL-CLC ogni 60secondi lasciando inalterate le altre condizioni al contorno (Il LOAD è impostato fisso a 5 KW x 6 spire= 30KW)\r\n\r\nSequenza Valori\r\n0% = 0W\r\n20% = 88KW\r\n40% = 176KW\r\n60% = 264KW\r\n80% = 352 KW --> non sarà soddisfatto perchè P_Set-point + PloadPmax > Pmax  \r\n100% = 440 KW --> non sarà soddisfatto perchè P_Set-point + PloadPmax > Pmax \r\n70% = 308 KW\r\n50% = 220 KW\r\n30% = 132 KW\r\n10% = 44 KW\r\n-10% = -44 KW\r\n50% = 220 KW\r\n10% = 44 KW\r\n70% = 308 KW\r\n100% = 440 KW --> non sarà soddisfatto perchè P_Set-point + PloadPmax > Pmax \r\n0% = 0W\r\n\r\nScopo: \r\nVerificare il nuovo software 1.6.7 e 0.14.7\r\nVerificare stabilità e precisione della regolazione e assenza oscillazioni.\r\nVerificare che il set-point sia sempre soddisfatto e che la regolazione avvenga entro 15sec\r\nTenere d\'occhio la distribuzione delle singole potenze dell\'inverter\r\nTenere d\'occhio la comunicazione per verificare assenza di condizioni anomale (fallback permessa).\r\n\r\nRISULTATI TEST\r\nFondamentalmente il set-point PEL-CLC è sempre rispettato.\r\nHa però evidenziato un certo caos nella erogazione delle singole potenze degli inverter e nel calcolo dei set-point interni.\r\nI set-point degli inverter non vengono comandati in modo distribuito:\r\n* all\'inizio, con set-point crescenti, c\'è almeno 1 inverter il cui set-point è basso.\r\n* quando si arriva al set-point 100% (non raggiungibile) tutti i set-point sono allineati al 100%.\r\n* quando il set-point viene diminuito, i set-point sono tutti differenti fra loro e diversi dalla parte iniziale.\r\n* quando il set-point va a valori negativi e si verifica un fallback da mancato target (corretto), alla successiva transizione di set-point i set-point tornano ad essere tutti allineati fra loro.\r\nComnque sulla potenza esportata, il set-point globale sembra soddisfatto.\r\n\r\nLA PROVA E\' SUPERATA sia con M2M che B24 ma CON RISERVA\r\n\r\n*Chiedo una valutazione al product management e conferma che questo comportamento potrebbe essere migliorato in WAVE 2.0 assicurando sempre una equa distribuzione dei set-point inverters.*',NULL,943,1,NULL,4,NULL,195,5,'2019-05-20 14:27:07','2020-02-21 14:32:29','2019-05-20',0,NULL,14935,14935,14,15,0,NULL,NULL,7529);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15265,2,305,'Applicando variazione lineare del carico con durata di 50sec, il setpoint PEL viene superato per tutta la transizione ','Test DVT eseguiti su impianto di prova vita\r\n7 PVS-50-TL configurati per CLC\r\n1 PVS-100-TL configurato per CLC\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000 (Pplan reale causa derating vs Vin = 360KW circa)\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nHo fatto numerose prove con B24 e M2M nelle quali tengo fisso il setpoint PEL e cambio il carico sia in modo lineare (graduale) che a step (brusco).\r\nHo allegato 4 report: all\'interno di ciascuno ci sono tutte le info relative alla prova e le immagini esplicative salvate.\r\n\r\nRISULTATI\r\n* Laddove il carico non cambia e rimane costante la regolazione rispetta sempre il set-point con la precisione dell\'1% PVPnom\r\n* La variazione brusca del carico esalta la regolazione del set-point PEL: il recupero e la regolazione del sety-point avviene sempre in una manciata di secondi.\r\n* La variazione graduale del carico invece mette in crisi la regolazione: se la velocità di variazione è di circa = tempo di variaizone dato del meter *10 o *20 allora durante la regolazione in diminuzione è\r\nprobabile che si verifichi un fallback da mancato target. Durante la regolazione in aumento di carico invece non ce la facciamo a regolare il set-point e la potenza esportata è minore. \r\nLa diminuzione del carico è ovviamente la situazione più critica: si oltrepassa sempre il set-point e si può generare il fallback.\r\n\r\nLe performance con meter M2M e B24 sono equivalenti con questo tipo di test.\r\nLa transizione con durata 50secondi mette in difficoltà la regoalzione: si va in fallback per non violare il tempo massimo di 15secondi.\r\nSi vedono almeno 2 condizioni di fallback (ZOOM1 e ZOOM2) e nello ZOOM1 si vedono addirittura 2 fallback consecutivi nella stessa transizione di carico.\r\nSe però si guarda tutta la transizione abbiamo uno slot complessivo di circa 45sec in cui il PEL è superato all\'interno del quale di va 2 volte in fallback.\r\n\r\nLA PROVA E\' SUPERATA CON RISERVA: \r\n\r\nRichiedo una valutazione al product management dei seguenti comportamenti:\r\n* una variazione lineare in discesa del carico con durata 50sec genera superamento del set-point e fallback a ripetizione. \r\n* Per tutti i 50secondi il PEL è oltre il limite e i 2 fallback che si verificano non fanno altro che \"spezzare\" l\'intervallo in 3 intervalli di durata singola <15sec. \r\n* Le performances rispecchiano il controllo implementato nel software wave 1.0.\r\n\r\n*VORREI SAPERE SE QUESTO COMPORTAMENTO VA MIGLIORATO NELLA WAVE 2.0*\r\n',NULL,943,1,195,5,NULL,195,2,'2019-05-20 15:09:29','2019-05-21 14:10:48','2019-05-20',0,NULL,14935,14935,16,17,0,NULL,NULL,7531);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15276,1,305,'PVS-100-TL LALLI: sporadicamente, a seguito di un aumento brusco di carico, il set-point PEL viene aggiornato dopo qualche decina di secondi. P-EXP rimane negativa a lungo','FW = 1909B\r\nSW = 0.14.2 \r\n\"elcFirmwareVersion\": \"0.3.9.6\",\r\n\"regulatorVersion\": \"5\"\r\n\r\nSet-point PEL = 0.01 con GB=0.01 (zero Power export)\r\n\"PVPlantNominalPowerW\": \"100000\",\r\n   \"activePower\": {\r\n        \"enabled\": true,\r\n        \"SetpointPercentage\": 0.01,\r\n        \"GuardbandPercentage\": 0.01,\r\n\r\n\r\nAlimentati 2 canali con 2 regatron con curve uguali 507Vocx26.5AIsc=13435W ciascuno per un totale di 26870W disponibili in INPUT.\r\nProgrammata una sequenza di variazione reale di load in accordo a \"LoadStep13LALLI_5min.xlsm\".\r\nLe variazioni di carico avvengono ogni 5 minuti: l\'intera sequenza dura 28000 secondi circa.\r\nEseguita prova per 48ore con passo campionamento di 5secondi\r\n\r\nObiettivi: \r\n* verificare che la P_EXP rispetti il set-point impostato\r\n* che durante le tranasizioni di carico la potenza venga regolata al set-point in tempi inferiori ai 15secondi.\r\n* che non ci siano fluttuazioni anomale.\r\n* che non ci siano errori di comunicazioni o stati di fallback ingiustificati\r\n\r\nRISULTATI:\r\n* Il set-point di potenza viene rispettato con la precisione di 1% PVP nom\r\n* Non ci sono fluttuazioni di potenza anomale.\r\n* Ci sono fluttuazioni in corrispondenza delle variazioni di carico ma il recvupero avviene sempre entro 15secondi.\r\n\r\nANOMALIE\r\nHo individuato due anomalie: la P-EXP (potenza esportata) rimane a lungo (decine di secondi) negativa dopo aumento di carico.\r\nLe letture di potenza del meter B23 vanno bene e rilevano subito una potenza negativa.\r\nL\'anomalia è che il set-point dell\'inverter viene aumentato solo dopo qualche decina di secondi.\r\n\r\n*METTO BASSA PRIORITA\' e nel CONTAINER della WAVE 2.0\r\nRICHIEDO VALUTAZIONE a FAGGIANI*\r\n\r\nAllego report con immagini esplicative.',NULL,943,1,NULL,3,NULL,195,0,'2019-05-22 10:33:53','2019-05-22 10:33:53','2019-05-22',0,NULL,14935,14935,18,19,0,NULL,NULL,7542);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15280,2,305,'PVS-100 LALLI: con Transizioni di P-IN multiple e ravvicinate si innescano OSCILLAZIONI nel controllo PEL','FW 1907B\r\nSW 0.14.0\r\n\"elcFirmwareVersion\": \"0.3.9.6\",\r\n\"regulatorVersion\": \"5\"\r\n\r\nCarico resistivo ipostato ad un taglio fisso di 4Kw\r\nSet-point PEL = 0.01 con GB=0.01 (zero Power export)\r\n\r\nProgrammata una sequenza di variazione reale di Pin in accordo a \"InputStepLalli Only input_1to25Kw\".\r\nLa potenza d\'ingresso viene fatta variare bruscamente tra Voc=580V, Isc=3A (1000W) a Voc=580V, Isc=61A (26500W).\r\n2 Step hanno durata di 60sec e 180sec e le altre di appena 20sec. \r\n\r\nObiettivo: verificare che la P_EXP rispetti il set-point impostato, che durante le tranasizioni di illuminazione la potenza venga regolata al set-point in tempi inferiori ai 10secondi.\r\nVerificare anche che la potenza Pin disponibile in MPPT richiesta per compensare il LOAD+S.P.PEL venga raggiunta in tempi più rapidi possibile.\r\n\r\nRISULTATI POSITIVI\r\nLe variazioni in salita di illuminazione vengono compesate corettamente: Il transitorio si esaurisce entro 15sec ed il PEL viene regolato in accordo al set-point.\r\nLe variazioni in discesa di illuminazione vengono gestite correttamente: PEL scende al di sotto del Set-point (non c\'è potenza sufficiente) ed il set-point interno va al 100%.\r\nIl set-point viene regolato molto velocemente.\r\n\r\nRISULTATI DA MIGLIORARE\r\nRipetendo più volte la transizione ad intervalli ravvicinati (20sec) si innescano delle oscillazioni sia sulla potenza che sul controllo che si estingono dopo diverse iterazioni.\r\n\r\nMETTO BASSA PRIORITA\' e nel CONTAINER della WAVE 2.0\r\nRICHIEDO VALUTAZIONE a FAGGIANI: può essere il sintomo di possibili problemi di oscillazione su impianti reali?\r\n\r\nAllego REPORT con immagini esplicative',NULL,943,1,NULL,3,NULL,195,1,'2019-05-22 12:44:41','2019-05-22 12:45:25','2019-05-22',0,NULL,14935,14935,20,21,0,NULL,NULL,7546);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15281,2,305,'PVS-100 LALLI: MPPT interferisce con performances PEL-CLC. La potenza richiesta per soddisfare il target PEL viene raggiunta in ritardo e P-EXP rimane sotto il target. ','FW 1907B\r\nSW 0.14.0\r\n\"elcFirmwareVersion\": \"0.3.9.6\",\r\n\"regulatorVersion\": \"5\"\r\n\r\nTest B\r\nCarico resistivo ipostato ad un taglio fisso di 24Kw\r\nSet-point PEL = 0.01 con GB=0.01 (zero Power export)\r\n\r\nTest C\r\nCarico resistivo ipostato ad un taglio fisso di 14Kw\r\nSet-point PEL = 0.11 con GB=0.01 (10% Power export)\r\n\r\nTest D\r\nCarico resistivo ipostato ad un taglio fisso di 4Kw\r\nSet-point PEL = 0.21 con GB=0.01 (20% Power export)\r\n\r\nProgrammata una sequenza di variazione reale di Pin in accordo a \"InputStepLalli Only input_1to25Kw\".\r\nLa potenza d\'ingresso viene fatta variare bruscamente tra Voc=580V, Isc=3A (1000W) a Voc=580V, Isc=61A (26500W).\r\n2 Step hanno durata di 60sec e 180sec e le altre di appena 20sec. \r\n\r\nObiettivo: verificare che la P_EXP rispetti il set-point impostato, che durante le tranasizioni di illuminazione la potenza venga regolata al set-point in tempi inferiori ai 10secondi.\r\nVerificare anche che la potenza Pin disponibile in MPPT richiesta per compensare il LOAD+S.P.PEL venga raggiunta in tempi più rapidi possibile.\r\n\r\nRISULTATI POSITIVI\r\nLe variazioni in salita di illuminazione vengono compesate corettamente: Il transitorio si esaurisce entro 15sec ed il PEL viene regolato in accordo al set-point.\r\nLe variazioni in discesa di illuminazione vengono gestite correttamente: PEL scende al di sotto del Set-point (non c\'è potenza sufficiente) ed il set-point interno va al 100%.\r\nIl set-point viene regolato molto velocemente.\r\nIl controllo PEL fa sempre il proprio dovere.\r\n\r\nRISULTATI DA MIGLIORARE\r\nPer compensare il carico e soddisfare il set-point PEL, quando la potenza d\'ingresso viene aumentata, il PVS-100 dovrebbe lavorare sempre al massimo. Dovrebbe velocemente essere estratta tutta la potenza disponibile in MPPT.\r\nLe impostazioni del set-point da parte del Q1 avvengono sempre correttamente ma l\'inverter, a causa dei meccanismi di MPPT, non riesce ad andare a lavorare al valore corretto di potenza.\r\nCi mette un certo tempo. Potrebbe essere migliorato lavorando sui meccanismi di Mppt del PVS-100.\r\n\r\n*METTO BASSA PRIORITA\' e nel CONTAINER della WAVE 2.0\r\nRICHIEDO VALUTAZIONE a BRACCIALI: nel prossimo rilascio è necessario verificare ed intervenore anche sulle perfrmances di MPPT del PVS-100 per velocizzare oppure vanno bene le performances attuali?*\r\n\r\nDa fare prova con PVS-50 per verificare la velocità MPPT vs PEL.\r\n\r\nAllego i report delle prove con all\'interno immagini esplicative.\r\n',NULL,944,1,195,3,NULL,195,3,'2019-05-22 12:58:05','2019-10-02 17:34:40','2019-05-22',0,NULL,14935,14935,22,23,0,NULL,NULL,7547);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15294,1,305,'Blocco permanente export limitation a seguito di problemi su rete ABB','Il giorno 2019/05/22 si sono verificati problemi di varia natura sulla rete LAN ABB (serrvizi di posta non funzionanti, accesso alle risorse di rete lento, lentezza nei programmi MICROSOFT in genere).\r\nNon sono in grado di individuare la tipologia di problemi, sarebbe meglio rivolgersi a IT.\r\n\r\nIl giorno 2019/05/23 ho trovato dei *problemi* *PERMANENTI* su due set-up DVT di export limitation.\r\n\r\n+Problema 1 Impianto REACT2 su Banco Bucci+\r\nConfigurazione\r\n2 REACT2-3.6-TL-OUTD con LAN connessa in deasy chain. SW Q1 = 0.5.7 e FW INV = 1906C\r\nQ1 44-ea-d8-a0-54-ba collegato a switch LAN, Q1 10-ce-a9-91-94-31 collegato in deasy-chain\r\nMeter M2M\r\nDescrizione problema\r\nHo trovato l\'impianto con Export limitation bloccata (in fallback evidente). Entrambi i Q1 non erano raggingibili via LAN tramite i loro consueti indirizzi IP.\r\nErano invece accessibili in access point: +sembravano perfettamente funzionanti ed anche connessi alla rete LAN con il solito indirizzo IP.+\r\nCome ho risolto il problema\r\nHo eseguito un rebbot solo del Q1 44-ea-d8-a0-54-ba che gestisce la deasy chain.\r\n+*Dopo il reboot tutto è tornato a funzionare.*+\r\n\r\nAllego REPORT con all\'interno immagini esplicative.\r\n\r\n+Problema 2 Impianto Prova vita MIZAR+\r\nConfigurazione\r\n7 MIZAR + 1 x simulare carico, 1 connesso allo switch LAN e gli altri in deasy chain. \r\nSW Q1 = 1.6.7 e FW INV = 1901B\r\n1 SIRIUS A connesso LAN direttamente allo switch LAN\r\nSW Q1 = 0.14.7 e FW INV = 1912B\r\nMeter M2M\r\nDescrizione problema\r\nHo trovato l\'impianto con gli inverter MIZAR in pasizione 1-3-4 funzionanti ed accessibili.\r\nGli inverter in posizione 4-5-6-7-9-10 non accessibili ed in stato di FALLBACK.\r\nErano invece accessibili in access point: +sembravano perfettamente funzionanti ed anche connessi alla rete LAN con il solito indirizzo IP.+\r\nCome ho risolto il problema\r\nHo eseguito un rebbot solo del Q1 dell\'inverter in posizione 4.\r\n+*Dopo il reboot tutto è tornato a funzionare.*+\r\n\r\nCOMMENTI-NOTE\r\n* *A mio parere il problema è molto grave perchè export limitation viene BLOCCATA in modo PERMANENTE. Problemi temporanei di varia natura della rete non devono bloccare la regolazione di potenza*\r\n* Accedendo in access point si nota che il Q1 bloccato non è consapevole di esserlo. Per lui la connessione LAN è operativa con il corretto indirizzo IP. In realtà la connessione non funziona. *Se in qualche modo potesse autodiagnosticare che la connessione LAN non è operativa, si potrebbe risolvere forzando il rebbot del Q1.*\r\n\r\nAssegno alta priorità e lo metto nel container del PRIMO RILASCIO perchè il problema è importante.\r\nChiedo a BRACCIALI di stabilire se bisogna affrontare subito o può essere fatto al secondo rilascio.\r\n*+Per decidere serve una disamina delle cause ed una anlisi dei rischi per i quali io non ho competenza.+*\r\n',NULL,943,4,195,6,NULL,195,10,'2019-05-23 14:20:05','2021-01-25 18:12:47','2019-05-23',100,NULL,14934,14934,26,27,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15307,1,305,'Mancata esecuzione attesa 60 secondi x rientro da Fallback (GUARD) x assenza comunicazione con meter','Ho riscontrato questo problema eseguendo i test di aggiornamento del SW del Q1 nel set-up di prova vita.\r\nPremetto che è a *bassa priorità*: lo segnalo solo per cercare di capire se potrebbe nascondere dei corner cases non coperti dal codice SW attuale.\r\n\r\nImpianto prova vita\r\n7 PVS-50-TL configurati per CLC con LAN in deasy chain\r\n1 PVS-100-TL configurato per CLC con LAN connessa a switch ethernet\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.7\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nUTITLIZZATO METER B24 gestito da INVERTER in POSIZIONE 10.\r\n\r\nSet-point CLC = 0.01 + Deadband = 0.01 (0% = KW)\r\nLOAD impostato fisso a 3.3kW x 6spire = 20KW\r\nLa potenza massima complessiva disponibile dell\'impianto è 360KW\r\nIl carico al massimo vale 20KW e Il set-point = 0 KW\r\nLa potenza massima da compensare è 20KW < Pmax_tot=360KW: L\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\nQueste impostazioni consentono di individuare eventuali perdite di set-point ed esecuzione si Pset-100% con superamento del set-point PEL impostato.\r\n\r\n*Eseguito test di programmazione del Q1 che gestisce il meter B24*:\r\nda v.1.6.7 a v.1.6.8 la prima volta\r\nda v.1.6.8 a v.1.6.8 la seconda e terza volta\r\n\r\nSCOPO del test\r\n* programmare il SW del Q1 che gestisce il meter \r\n* verificare che durante il riavvio tutti gli inverter vadano in fallback e non al 100% potenza.\r\n* verificare che durante il riavvio, l\'inverter programmato continui quantomeno a eseguire il precedente set-point senza andare al 100%.\r\n* verificare che nel processo di programmazione il set-point PEL non sia mai superato per un tempo superiore a 10sec.\r\n* verificare che al termine della programmazione il sistema torni a regolare correttamente il set-point.\r\n* verificare la distribuzione delle potenze fra gli inverter prima, durante e dopo la programmazione.\r\n* Verificare riproducibilità del comportamento facedno il test più volte.\r\n\r\nRISULTATI POSITIVI\r\n* La programmazione va a buon fine senza problemi.\r\n* durante il riavvio del Q1 vanno in fallback tutti gli inverter coinvolti nel controllo con set-point a zero: è garantito che il PEL non venga superato.\r\n* quando il riavvio è completato ed la comunicazione con il meter ripristinata, viene atteso correttamente un tempo di 60sec come previsto dalla gestione del fallback (GUARD).\r\n* quando export limitation torna a regolare il set-point, c\'è un overshOot molto elevato di potenza che però viene compensato entro 10 secondi.\r\n* il test è stato eseguito 3 volte: stesso comportamento nei 3 casi. Ottima riproducibilità.\r\n\r\nANOMALIA INDIVIDUATA\r\nIl PVS-100 la prima volta non esegue il tempo di attesa di 60sec dopo il riavvio del Q1 dell\'inverter in POS 10 che gestisce il B24. Le altre 2 volte successive invece si.\r\nAnalizzando i dati si vede che viene segnalato lo stato di GUARD ciè quello da fallback per mancanza comunicazione con meter.\r\n\r\nAllego report con immagini esplicative e dati.\r\n',NULL,943,4,195,3,NULL,195,1,'2019-05-25 10:03:26','2020-02-21 14:38:36','2019-05-25',0,NULL,14935,14935,24,25,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15308,1,305,'Dopo programmazione SW Q1 SIRIUS, la WEB-UI è impallata non funziona. E\' necessario un ON-OFF tensione DC inverter per tornare alla normalità','Ho trovato questo problema facendo i test di programmazione SW Q1 nel set-up di prova vita.\r\n\r\nImpianto prova vita\r\n7 PVS-50-TL configurati per CLC con LAN in deasy chain\r\n1 PVS-100-TL configurato per CLC con LAN connessa direttamente a switch ethernet\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.7 --> 1.6.8\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.7 --> 0.14.8\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nUTITLIZZATO METER B24\r\n\r\nSet-point CLC = 0.01 + Deadband = 0.01 (0% = KW)\r\nLOAD impostato fisso a 3.3kW x 6spire = 20KW\r\nLa potenza massima complessiva disponibile dell\'impianto è 360KW\r\nIl carico al massimo vale 20KW Il set-point = 0 KW: La potenza massima da compensare è 20KW < Pmax_tot=360KW\r\nL\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\nQueste impostazioni consentono di individuare eventuali perdite di set-point ed esecuzione si Pset-100% con superamento del set-point PEL impostato.\r\n\r\n*Eseguito test di programmazione del Q1 di un inverter che non gestisce il meter B24 (abbiamo scelto il PVS-100 SIRIUS)*\r\nda v.0.14.7 a v.0.14.8 la prima programmazione\r\nda v.0.14.8 a v.0.14.8 le successive\r\n\r\nSCOPO del test\r\n* programmare il SW del Q1 che non gestisce il meter.\r\n* verificare che durante il riavvio l\'inverter collegato a quel Q1 continui ad eseguire quantomeno il precedente set-point senza andare al 100%. \r\n* verificare che durante il riavvio, gli inverter a valle di quel Q1 vadano in Fallback. \r\n* se tra gli inverter a valle c\'è anche quello che gestisce il meter allora devono andare in fallback anche gli inverter a monte.\r\n* se tra gli inverter a valle non c\'è quello che gestisce il meter allora gli inverter a monte devono cercare di regolare il set-point di PEL.\r\n* verificare che nel processo di programmazione il set-point PEL non sia mai superato per un tempo superiore a 10sec.\r\n* verificare che al termine della programmazione il sistema torni a regolare correttamente il set-point.\r\n* verificare la distribuzione delle potenze fra gli inverter prima, durante e dopo la programmazione.\r\n* Verificare riproducibilità del comportamento facedno il test più volte.\r\n\r\nRISULTATI POSITIVI\r\n* La prima programmazione va a buon fine. la seconda va a buonfine ma la web-ui non è più accessibile (ANOMALIA).\r\n* siamo nel caso in cui si sta programmando il PVS-100 che non è in deasy chain con nessuno.\r\n* durante il riavvio del Q1 nessun inverter va in fallback ed il set-point continua ad essere regolato\r\n* Non ci si accorge nemmeno di aver fatto la programmazione.\r\n* il test è stato eseguito 2 volte: stesso comportamento.\r\n\r\n*ANOMALIA RILEVATA*\r\n* *la seconda programmazione del PVS-100 va a buonfine, le API rispondono, export limitation funziona +ma la web-ui non è più accessibile+*\r\n* *E\' necessario un reset della tensione DC dell\'inverter per risolvere e far ripartire la web-ui*\r\n\r\nAllego report con immagini esplicative',NULL,943,2,3,5,NULL,195,3,'2019-05-25 10:17:32','2019-05-28 14:47:26','2019-05-25',0,NULL,NULL,15308,1,2,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15310,1,305,'Dopo programmazione di tutti i Q1 dell\'impianto, 2 inverter su 8 sono in delinquency e non partecipano più a export limitation','Ho trovato questo problema facendo i test di programmazione nell\'impianto misto di prova vita.\r\n7 PVS-50-TL configurati per CLC con LAN in deasy chain\r\n1 PVS-100-TL configurato per CLC con LAN connessa direttamente a switch ethernet\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.7 --> 1.6.8\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.7 --> 0.14.8\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nUTITLIZZATO METER M2M gestito da INV in POS 3\r\n\r\nSet-point CLC = 0.01 + Deadband = 0.01 (0% = KW)\r\nLOAD impostato fisso a 60kW x 6spire = 180KW \r\nLa potenza massima complessiva disponibile dell\'impianto è 360KW, Il carico al massimo vale 180KW, Il set-point = 0 KW, La potenza massima da compensare è 180KW < Pmax_tot=360KW\r\nL\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\n\r\nEseguito test di programmazione di tutti i Q1 in questo ordine (dall\'ultimo verso il primo della deasy chain):\r\nPVS-50 --> INV-10, 7, 6, 5, 4, 3 del Q1 di un inverter che non gestisce il meter\r\nda v.1.6.7 a v.1.6.8\r\nda v.1.6.8 a v.1.6.8\r\nNon riporgrammo l\'INV-1 perchè è quello che interrogo con ATP per scaricare i dati.\r\n\r\nPVS-100 --> INV-2\r\nda 0.14.8 a 0.14.8\r\n\r\nSCOPO del test\r\n* programmare tutti i Q1 lanciando le programmazione in sequenza tramite i pannelloi WEB UI, rispettando la deasy chain.\r\n* Non riporgrammo l\'INV-1 perchè è quello che interrogo con ATP per scaricare i dati.\r\n* verificare che nel processo di programmazione il set-point PEL non sia mai superato per un tempo superiore a 10sec.\r\n* verificare che al termine della programmazione il sistema torni a regolare correttamente il set-point.\r\n* verificare la distribuzione delle potenze fra gli inverter prima, durante e dopo la programmazione.\r\n* Verificare riproducibilità del comportamento facendo il test più volte.\r\n \r\nRISULTATI POSITIVI\r\n* Le programmazioni vanno a buon fine.\r\n* Il sistema tenta di regolare il set-point finchè non viene programmato il Q1 che gestisce il meter.\r\n* Successivamente gli inverter vanno in fallback e attendono 60secondi dal ripristino della comunicazione con il meter.\r\n* Al termine il set-point di export limitation viene regolato coinvolgendo la maggior parte degli inverter.\r\n\r\nRISULTATI NEGATIVI\r\n* *al termine della programmazione non tutti gli inverter sono tornati a partecipare all\'export limitation.*\r\n* *gli inverter in POS 6 (c7:d1) e in POS 8 (20:d2) sono costantemente in fallback. Gli altri inverter o non li vedono, o li vedono in stato \"delinquent\"*\r\n* Analizzando il problema con Faggiani, sembra che abbiano avuto un problema nella gestione del comando MULTICAST che permette loro di interagire.\r\n* E\' stato necessario fare un Reboot per tornare alla normalità.\r\n\r\nPROVA NON SUPERATA\r\n\r\nAllego report con immagini esplicative',NULL,943,4,195,6,NULL,195,3,'2019-05-25 11:09:34','2019-10-07 19:42:37','2019-05-25',100,NULL,14935,14935,26,27,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15311,1,305,'Q1 MIZAR v.1.6.9 e Q1 SIRIUS v.0.14.9 --> Tentando di programmare l\'inverter con la stessa versione FW, il processo non viene eseguito e viene generato un messaggio di errore inappropriato','Ho trovato questo problema durante i test di programmazione sul set-up misto di prova vita.\r\n\r\nImpianto prova vita\r\n7 PVS-50-TL configurati per CLC con LAN in deasy chain\r\n1 PVS-100-TL configurato per CLC con LAN connessa direttamente a switch ethernet\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.9\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.9\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nUTITLIZZATO METER B24 gestito da INV in POS 10 (0a:de)\r\n\r\nSet-point CLC = 0.01 + Deadband = 0.01 (0% = KW)\r\nLOAD impostato fisso a 60kW x 6spire = 180KW \r\nLa potenza massima complessiva disponibile dell\'impianto è 360KW, Il carico al massimo vale 180KW, Il set-point = 0 KW\r\nLa potenza massima da compensare è 180KW < Pmax_tot=360KW\r\nL\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\n\r\nEseguito test di programmazione del FW di 3 INVERTERS in questo ordine:\r\n* INV POS-10 (0a:de) che gestisce il Meter. FW 1901B --> 1901B\r\n* INV POS-6 (c7:d1). FW 1901B --> 1901B\r\n* INV POS-2 (8a:38) SIRIUS. FW 1912B --> 1912B\r\n\r\nNon riporgrammo l\'INV in POS-1 (da:13) perchè è quello che interrogo con ATP per scaricare i dati.\r\n\r\nSCOPO del test\r\n* programmare il FW di almeno 3 Inverter fra cui quello che gestisce il meter.\r\n* verificare che nel processo di programmazione il set-point PEL non sia mai superato per un tempo superiore a 10sec.\r\n* verificare che al termine della programmazione il sistema torni a regolare correttamente il set-point.\r\n* verificare la distribuzione delle potenze fra gli inverter prima, durante e dopo la programmazione.\r\n* Verificare riproducibilità del comportamento facendo il test più volte.\r\n \r\nRISULTATI POSITIVI\r\nNessuno\r\n\r\n\r\nRISULTATI NEGATIVI\r\nHo tentato come sempre di riprogrammare il FW con la stessa versione tramite selzione del file in locale.\r\nLa programmazione del FW è stata schedulata ma non è partita: finito il time-out mi ha detto che non c\'era nessun firmware disponibile e mi invitava a riporvare.\r\nAl successivo tentativo la web UI mi ha dato un messaggio di errore.\r\n*NON E\' POSSIBILE RIPROGRAMMARE IL FIRMWARE DELL\'INVERTER*\r\n\r\n*Ho eseguito il test 2 volte, facendo anche un Reset global impianto tramite DC ON-OFF.*\r\n\r\nNOTA: durante questo test avevo ATP in funzione e stavo interrogando l\'INV in POS-1.\r\nNOTA2: avevo anche la wifi accesa connessa in access point al REACT e la LAN connessa alla rete ABB.\r\n\r\n*PROVA FALLITA*\r\nAllego Report con immagini esplicative\r\n\r\nPriorità risoluzione *IMMEDIATA*\r\n',NULL,943,4,195,3,NULL,195,5,'2019-05-25 14:50:03','2019-10-03 14:58:40','2019-05-25',0,NULL,14935,14935,28,29,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15312,1,305,'Inviando comando \"Inverter Reboot\" a 5 inverter su 8, dopo il riavvio 3 inverter sono \"delinquent\" ed in stato \"guard\"','Problema trovato facendo i test di Reboot Inverter tramite WEB UI su impianto misto prova vita\r\n\r\n7 PVS-50-TL configurati per CLC con LAN in deasy chain\r\n1 PVS-100-TL configurato per CLC con LAN connessa direttamente a switch ethernet\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.9\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.9\r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nUTITLIZZATO METER M2M gestito da INV in POS 3 (a2:e4)\r\n\r\nSet-point CLC = 0.01 + Deadband = 0.01 (0% = KW)\r\nLOAD impostato fisso a 60kW x 6spire = 180KW \r\nLa potenza massima complessiva disponibile dell\'impianto è 360KW, Il carico al massimo vale 180KW, Il set-point = 0 KW\r\nLa potenza massima da compensare è 180KW < Pmax_tot=360KW\r\nL\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\n\r\n*Eseguito test di INVERTER FIRMWARE REBOOT inviato tramite WEB-UI agli inverter INV in POS-2 (SIRIUS), POS-3 (Gestisce Inverter), POS-4, POS-5 e POS-8 (5 su 8)*\r\n\r\nSCOPO del test\r\n* al termine delle procedure di reboot dell\'inverter la regolazione PEL deve essere garantita, nessun INV deve essere in errore, tutti devono partecipare.\r\n* Ci possono essere transitori oltre il set-point che non devono durare più di 10secondi.\r\n \r\nRISULTATI NEGATIVI\r\n*Al termine del riavvio di tutti gli inverter su cui abbiamo eseguito un software reboot tramite web-UI, ma non tutti gli inverters partecipano al controllo PEL.\r\nGli INV in POS-2 (SIRIUS), POS-4 e POS-5 sono in FALLBACK (GUARD). --> vedi file Excel.*\r\nGLi altri sono tutti operativi.\r\n\r\nRISULTATO POSITIVO\r\nDopo circa 10 minuti i 3 che non erano operativi tornano ad esserlo nuovamente e tutto torna a funzionare come in precedenza.\r\n\r\n\r\nPROVA DA ESEGUIRE NUOVAMENTE e DAI DUBBI RISULTATI\r\n\r\nAllego REPORT provvisorio',NULL,942,4,195,5,NULL,195,4,'2019-05-25 17:33:53','2021-01-26 12:27:43','2019-05-25',100,NULL,14935,14935,30,31,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15345,2,305,'Se ELC si blocca a lungo su 1 o più Q1 che non gestiscono il METER, il set-point di PEL può essere superato in modo permanente','Ho trovato questo problema facendo test DVT sul set-up misto di prova vita Mizar.\r\n\r\n*LA PROBLEMATICA DESCRITTA IN QUESTO TICKET E\' STATA GIA\' INDIVIDUATA IN ALTRI TEST CASE ED ESPERIMENTI RIPORTATI NEI \"TICKET_15216\":http://monitoring-helpdesk.it.abb.com/issues/15216 E \"TICKET_14725\":http://monitoring-helpdesk.it.abb.com/issues/14725.*\r\n*HO CREATO QUESTO TICKET PERCHE\' QUESTO TEST CASE E\' DIFFERENTE DAGLI ALTRI.*\r\n\r\nImpianto prova vita\r\n7 PVS-50-TL configurati per CLC\r\n1 PVS-100-TL configurato per CLC\r\n1 INV PVS-50-TL configurato come carico (P_LOAD = P_INV9 x 6spire)\r\nPplan = 440000\r\nPplan reale causa derating vs Vin = 360KW circa\r\n\r\nPVS-50-TL\r\nSW Q1 = 1.6.9\r\n\"elcFirmwareVersion\": \"0.4.6\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1901B\r\n\r\nPVS-100-TL\r\nInput independent. Alimentati solo 3 canali su 6. --> Pmax circa 50KW\r\nSW Q1 = 0.14.9\r\n\"elcFirmwareVersion\": \"0.4.6\",\r\n\"regulatorVersion\": \"5\"\r\nFW Inverter = 1912B\r\n\r\nCONFIGURATO UTILIZZO con METER M2M\r\n\r\nSet-point CLC = 0.06 + Deadband = 0.01 (5% = 22KW)\r\nLOAD impostato fisso al 15% --> 8.25kW (in base a derating) x 6spire = 50KW.\r\nLa potenza massima complessiva disponibile dell\'impianto è 360-400KW (in base a derating)\r\nLa potenza massima da compensare è 50KW+22KW= 72 KW < Pmax_tot=360-400KW. L\'impianto ha quindi potenza sempre sufficiciente per compensare e regolare il PEL setpoint.\r\n\r\nQueste condizioni fanno si che se 2 o più inverter smettono di parteciapre a PEL e la loro potenza va al 100%, il setpoint di PEL viene superato permanentemente.\r\n\r\nEseguita prova nel corso della quale abbiamo:\r\n* Bloccato il sw di export limitation tramite SSH con comando \"supervisorctl stop elc_main\" su almeno 3 Q1 che non gestiscono il meter.\r\n* atteso che il sistema reagisca. \r\n* Sbloccato nuovamente il sw di export limitation tramite SSH con comando \"supervisorctl start elc_main\".\r\n* atteso che il sistema reagisca e ritorni a funzionare nelle condizioni iniziali.\r\n\r\nRISULTATI POSITIVI\r\nBloccando ELC su INV-4, INV-5 e INV-6, dopo 2 minnuti i tre inverter sono andati a potenza massima (esaurito time-out).\r\nil sistema ha provato ad abbassare a 0 i set-point degli altri ma il PEL è rimasto oltre il limite per le condizioni della prova.\r\nDopo 15secondi gli inverter con ELC abilitato sono andati correttamente in FALLBACK da mancato target.\r\nDopo il riavvio di ELC, tutti gli inverter sono tornati a partecipare al controllo.\r\n\r\nRISULTATI DA MIGLIORARE\r\nIl set-point è superato a lungo ed in modo costante. \r\n\r\nSOLUZIONE DISCUSSA INSIEME\r\nShort term solution\r\na)	*Quando Export limitation viene impostata ed attivata, nel momento dell’avvio dell’eseguibile, viene inviato un comando permanente (forever) di P_reduction con argomento=0%, smooth=0, timeout=255*.\r\nIl comando, impedisce nall’inverter di erogare potenza incontrollata e quindi di superare il valore di export limitation.\r\nb)	Considerata la soluzione suddetta, bisogna anche ripristinare il valore di time-out di 1 minuto nel comando temporaneo usato da export limitation (attualmente è 3minuti).\r\nc)	Quando Export limitation viene impostata e *disattivata*, è necessario inviare un *comando permanente (forever) di P_reduction con argomento=100%, smooth=0, timeout=255*, per impedire problemi di limitazione involontaria della potenza dell’impianto.\r\nd)	*Queste operazioni devono essere gestite dal software Q1*.\r\n\r\nMedium term solution\r\ne)	Tutti i punti descritti nella short term solution.\r\nf)	A nostro avviso, quando Export limitation è attivo, non dovrebbe essere possibile eseguire altri comandi di P_reduction forever inviati dall’esterno. \r\ng)	Tramite software Q1, lato back-end, si potrebbe fare in modo che comandi di P_reduction_forever inviati tramite WEB-UI o server SUNSPEC non vengano trasmessi se export limitation è attiva.\r\nh)	Rimarrebbero scoperti i comando inviati su RS485 (protocollo Aurora o Modbus) gestiti da MICRO SUPERVISOR. \r\nSi potrebbe far inviare dal Q1 al Micro un flag per segnalare quando export limitation è attiva: in tal caso il MICRO non dovrebbe eseguire comandi P_reduction_forever.\r\nQuesta strategia non si concilierebbe con il punto a) ma il problema verrebbe risolto facendo inviare dal Q1 il comando P_reduction_forever=0% prima di attivare il flag di export limitation.  \r\ni)	Queste soluzioni devono essere valutate da Botarelli e Faggiani.\r\n\r\n\r\n\r\n',NULL,943,1,NULL,5,NULL,195,1,'2019-05-29 15:34:52','2020-02-21 14:44:55','2019-05-29',0,NULL,14935,14935,32,33,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15373,2,305,'La programmazione del FW Inverter genera delle criticità come oscillazioni prolungate del P-EXP e distribuzione molto asimmetrica delle Potenze','La fase di programmazione degli inverter è particolarmente critica per Export limitation:\r\n* è una fase lunga (15-20min) in cui si possono generare stati di delinquency o Fallback per lunghi periodi\r\n* è una fase in cui il Q1 impegnato nella programmazione può non gestire in modo efficiente la deasy chain e la comunicazione dei pacchetti di export limitation.\r\n\r\nAllego i report di due test cases che ho fatto:\r\n# *Ho programmato 2 inverter su 8, uno dei quali gestisce il meter M2M*.\r\nRISULTATI POSITIVI\r\nLa programmazione va a buon fine nei tempi previsti (15-20min).\r\nAl termine della programmazione tutti gli inverter tornano a partecipare al controllo: nessuno è in fallback, i set-point sono plausibili.\r\nC\'è un lasso di tempo in cui, durante la programmazione del Q1 che gestisce il meter, il meter smette di comunicare: gli inverter vanno regolarmente in fallback.\r\nSi verificano overshoot oltre il set-point di PEL ma la durata è inferiore a 10sec.\r\nNon ho rilevato perdita di comunicazione nel Q1 INV-1 che interrogo con ATP.\r\nRISULTATI NEGATIVI\r\n*Si sono verificate forti oscillazioni del P-EXP nel corso delle prime fasi della programmazione: hanno avuto picchi consistenti e sono ripetute.*\r\n*La fase di fallback è durata molto (400sec): se Q1 durante la programmazione \"chiude\" ELC allora è spiegato (da verificare).*\r\nC\'è stata una segnalazione da mancato target.\r\n# *Ho programmato tutti gli inverter dell\'impianto di prova vita*\r\nRISULTATI POSITIVI\r\nLa programmazione va a buon fine nei tempi previsti (30 min complessivi).\r\nAl termine della programmazione tutti gli inverter tornano a partecipare al controllo: nessuno è in fallback, i set-point sono molto disallineati, potrebbero essere migliori.\r\nC\'è un lasso di tempo in cui, durante la programmazione del Q1 che gestisce il meter, il meter smette di comunicare: gli inverter vanno regolarmente in fallback.\r\nSi sono verificate oscillazioni del P-EXP dovute a spengimento e riaccensione inverter programmati, perdita di comunicazione con Meter: si verificano overshoot oltre il set-point di PEL nel momento in cui gli inverter programmati si riaccendono ma la durata è inferiore a 10sec.\r\nNon ho rilevato perdita di comunicazione nel Q1 INV-1 che interrogo con ATP.\r\nRISULTATI NEGATIVI\r\n*Alla fine della programmazione i set-point sono estremamente differenti rispetto all\'inizio della prova. 2 inverter ad esempio sono regolati a potenza zero.*\r\n\r\nDiscutendo di questo con Alessandro, abbiamo stimato che le criticità derivato dal tentativo di fare export limitation anche da parte di quegli inverter che subiscono la programmazione.\r\nPotremmo pensare di inserire un altro stato di fallback per l\'inverter sotto programmazione che inizia e finisce con la programmazione stessa.\r\nQuesto farebbe si di avere meno oscillazioni e tentativi.\r\n\r\nInoltre è necessario stimare la capacità del Q1 di gestire contemporaneamente Programmazione, Deasy chain e export limitation. Se non ce la fa (come sembra) sarebbe meglio bloccare se stesso e la deasy chain a valle in modo permanente per tutto il processo. Questo metterebbe il suo inverter e gli inverter a valle in fallback costante per tutta la programmazione.\r\nIl comportamente sarebbe riproducibile.\r\n ',NULL,943,1,NULL,4,NULL,195,4,'2019-05-31 15:39:38','2019-06-03 14:31:43','2019-05-31',0,NULL,14935,14935,34,35,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15386,1,305,'Nel momento del ripristino potenza dopo FALLBACK (GUARD) per mancata comunicazione con meter, MPPT interferisce con il set-point ELC impostato impedendone il raggiungimento.','Questo problema è stato trovato nel set-up PVS-120-TL-480 di LALLI *facendo 4 aggiornamenti consecutivi del FW Q1*.\r\n*METTO IL TICKET IN FEEDBACK A BRACCIALI PER SAPERE SE E\' TOLLERABILE OPPURE SE E\' NECESSARIO INTERVENIRE SUL FW INVERTER IN WAVE 2.0.*\r\n\r\nNel set-up è presente 1 solo PVS-120-TL-480 connesso a trafo 480-400.\r\nMeter utilizzato B23 inserito per ovvi motivi sul lato 400V del trasfromatore. Il carico resistivo è sul lato 400V.\r\n\r\nFW INV = 1912C\r\nFW Q1 prima di aggiornamento = 0.14.7 \r\n\"elcFirmwareVersion\": \"0.4.4\",\r\n\"regulatorVersion\": \"5\"\r\nFW Q1 dopo aggiornamento = 0.14.9\r\n\"elcFirmwareVersion\": \"0.4.6\",\r\n\"regulatorVersion\": \"5\"\r\n\r\nRegatron 1 e 2 con Pin disponibile di 13000W (520Vmpp-25Ampp) per un totale di 26000W.\r\nALimentati 2 canali (ch1 e ch3) \r\n\r\nPVP plan = 120000\r\n\"SetpointPercentage\": 0.09, \"GuardbandPercentage\": 0.01\r\n--> Set-point CLC = 8% di 120000 = 9600W\r\nCarico Resistivo fisso di 10KW\r\n--> la potenza da compensare è 19600 ed è inferiore a Pmax_setup = 26000W.\r\npertanto mi aspetto che il set-point PEL sia sempre soddisfatto.\r\n\r\n*Eseguito test di programmazione Q1 per 4 volte di fila da 0.14.7 a 0.14.9 la prima volta, da 0.14.9 a 0.14.9 le altre.*\r\n\r\nScopo: \r\nVerificare che il processo di aggiornamento vada a buon fine.\r\nVerificare che i meccanismi di compensazione del set-point e gestione del fallback funzionino correttamente.\r\nVerificare stabilità e precisione della regolazione e assenza oscillazioni quando export limitation è in funzione.\r\nVerificare riproducibilità del comportamento facendo il test più volte.\r\nNel caso di overshoot oltre il set-point, non deve durare più di 10 secondi.\r\nVerificare ripartizione delle potenze tra i vari inverter componenti l\'impianto.\r\nVerificare che al termine del processo, export limitation ritorni a funzionare correttamente come prima dell\'avvio del processo di aggiornamento.\r\n\r\nRISULTATI POSITIVI\r\nLa programmazione va sempre a buon fine nei tempi previsti (qualche minuto).\r\nDurante il riavvio del Q1 (e quindi anche di export limitation) il set-point viene mantenuto al valore precedente grazie al time-out di 2 minuti del comando temporaneo. Sembra quindi si continui a regolare.\r\nIl comportamento è riproducibile.\r\nCi sono oversoot di P-EXP oltre il set-point ma vengono recuperate entro qualche secondo.\r\nAl termine della prova la regolazione di EXPORT LIMITATION riprende correttamente.\r\n\r\n\r\nRISULTATI NEGATIVI\r\n\r\nAl momento della connessione dell\'inverter purtroppo si va subito in fallback. Problema a priorità bassa già tracciato nel ticket \"RM-15216\":http://monitoring-helpdesk.it.abb.com/issues/15216\r\n\r\n*In uscita dal Fallback (dopo 60secondi) il set-point dell\'inverter viene aumentato da 0% a 100% ma l\'inverter è incapace di raggiungere la potenza programmata*.\r\n*L\'MPPT blocca gli ingressi della a 350Vdc-26Adc= 9100W --> 18000W che non sono sufficienti a compensare 19600W*.\r\n+*Dopo qualche minuto l\'MPPT si sblocca e riporta la potenza al valore corretto in limitazione*+.\r\nNon è responsabilità del controllo ma delle dinamiche di MPPT del PVS-100/120: sembra che la fase di entrata e di uscita dal fallback interferisca non bene con il riferimento di MPPT.\r\n\r\n*Analisi: MPPT del PVS-100 potrebbe avere problemi di congelamento e ripristino di MPPTref quando si eseguono manovre di Power reduction da 100% a 0% e viceversa con smooth=0sec.*\r\n\r\nPROVA SUPERATA perchè alla fine la potenza si sblocca e si torna a regolare il PEL ma RISULTATI DA MIGLIORARE\r\n\r\nAllego REPORT con immagini esplicative.',NULL,944,4,195,4,NULL,195,4,'2019-06-04 15:31:22','2019-10-02 17:36:05','2019-06-04',0,NULL,14935,14935,36,37,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15497,1,305,'invio configurazione Export Limitation a portale','Nella configurazione inviata a portale relativa all\'Export Limitation non è presente il valore della banda di guardia.',NULL,942,4,3,6,NULL,125,2,'2019-06-24 14:43:05','2020-02-21 14:48:40','2019-06-24',0,NULL,14935,14935,38,39,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15502,2,305,'profilazione impostazioni Export Limitation','Occorre limitare i range di configurabilità delle variabili di Export Limitation in modo da evitare situazioni non congrue del controllo (ad esempio guard band a 0%).\r\n\r\nInoltre valutare se possibile precompilare alcuni campi delle impostazioni (Set point, fallback, guard band...).\r\n\r\nSimone',NULL,943,1,3,6,NULL,125,4,'2019-06-25 12:10:26','2020-03-13 15:59:28','2019-06-25',0,NULL,14935,14935,40,41,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (15943,2,305,'2 schede Q1 bloccate in prova vita Mizar (test export limitation in corso)','Abbiamo avuto un problema con due schede Q1@1GHz in prova vita.\r\n\r\nPrima di andare in ferie tutte le schede erano operative e raggiungibili. Al ritorno dalle ferie (prima settimana di settembre) une di esse non si raggiugneva né tramite Access Point né tramite ethernet. Tutte le altre si. Il problema non è stato analizzato subito, ma siamo arrivato ad oggi.\r\nQuesta mattina abbiamo spento la prova vita ed estratto la scheda. Alessandro F. l’ha analizzata portandola in laboratorio e collegandosi in seriale.\r\n*La scheda carica il boot correttamente e e anche il kernel, dopodichè arrivano una serie di messaggi in loop che ti allego nella immagine img1. In questa condizione come dicevo non c’è Access Point o interfacce di rete.* \r\nLa scheda è stata dunque sostituita e la prova vita riaccesa, ma a quel punto abbiamo notato che una seconda scheda non era più raggiungibile.\r\nAnalizzata pure quella, sembra avere lo stesso problema (vedi img2).\r\n\r\nLe schede sono disponibili presso banco Faggiani.\r\n\r\n',NULL,943,1,18,6,NULL,195,2,'2019-09-12 18:39:25','2019-09-13 14:28:28','2019-09-12',0,NULL,14935,14935,42,43,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (16019,2,305,'Export Limitation in zero power dopo aggiornamento FW','A seguito di aggiornamento FW degli inverter PVS-100 alla versione 0.14.12 della connettivity nei seguenti impianti:\r\n\r\n- CEF Ploiesti (Site EID 19083007) \r\n\r\n- CEF Penny Lehliu (Site EID 17646026)\r\n\r\n- CEF Ghencea (Site Entity 19422874)\r\n\r\n- CEF Pantelimon (Site Entity 19853092)\r\n\r\nExport Limitation non funziona correttamente.\r\n\r\nGli inverter sono bloccati a 250W di produzione nonostante la lettura della potenza attiva sul meter consenta una maggior erogazione di potenza\r\n\r\nGli impianti eseguivano correttamente la funzione di Export Limitation prima dell\'aggiornamento FW.\r\n\r\nNOTA: gli inverter non hanno perso la configurazione nè sono in fallback.\r\n\r\nIn allegato mail del cliente con link alla WUI locale di un inverter e meter\r\n\r\nSimone\r\n',NULL,942,5,125,7,NULL,125,3,'2019-09-25 12:04:54','2020-03-09 09:27:24','2019-09-25',0,NULL,NULL,16019,1,2,0,'2020-03-09 09:27:24',NULL,NULL);
