INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (16618,1,305,'valore percentuale reale di potenza attiva diverso dal set point','installer app 2.1.15 beta\r\nlogger 1.8.0\r\ntib 1902B\r\n\r\ndurante le prove di exp. limit, ho dovuto cambiare il set point (con i seguenti step 100% -->75%-->50%-->25%-->0%) della potenza attiva dell\'inverter in pos. 9, che funge da carico.\r\nFacendo ciò, si evidenzia che la potenza reale è molto diversa dalle percentuali attese, soprattutto con valori di set point vicini al 100%.\r\n',NULL,943,4,10,39,NULL,10,3,'2019-11-27 14:04:15','2020-02-21 16:59:30','2019-11-27',0,NULL,NULL,16618,1,2,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (16620,1,305,'con un Q1 con \"problemi di MMC\", l\'mmissione in rete di potenza limitata da exp. limit, non è più garantita','installer app 2.1.15 beta\r\nlogger 1.8.0\r\ntib 1902B\r\n\r\ndurante le prove di exp. limit con impostazione 0% e guard a 2% (della Pnom. 440kW); correttamente la potenza letta sul wattmetro, per come è fatto l\'impianto è di -9kW. \r\nA questo punto è successo di perdere l\'AP di un Q1.\r\nDopo indagine preliminare, si è notato che era dovuto a problemi di MMC (già documentato nel ticket #16605)\r\n\r\nA seguito di questo sono accadute 2 cose:\r\n\r\n1- perdita di 7 indirizzi IP (erano in DHCP) su 9\r\n2- la potenza in rete oscillava continuamente (comunque tra 2 valori non sempre gli stessi) fino a raggiungere anche il centinaio di kW (letti sul wattmetro), quindi la potenza immessa in rete non era quella che ci aspettavamo dai settaggi iniziali di exp. limit',NULL,943,4,10,5,NULL,10,3,'2019-11-27 14:23:53','2019-12-18 16:45:23','2019-11-27',0,NULL,NULL,16620,1,2,0,NULL,NULL,NULL);
INSERT INTO `issues` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (16775,1,305,'Possibile scopertura gestione Export Limitation','Q1 1.6.15\r\n\r\nDi seguito descrivo una potenziale scopertura,forse non prevista in fase di definizione della specifica, in cui mi sono imbattuto eseguendo prove di Export Limitation.\r\n\r\nCon 1 inverter e 1 meter M2M partecipanti all\'export limitation (e correttamente configurati sull\'app \"Installer for Solar Inverters\") ho eseguito le seguenti operazioni:\r\n\r\n1) Spento l\'inverter e rimosso il meter (spento e scollegato dalla LAN)\r\n2) Riacceso l\'inverter --> questo non esporta potenza in quanto non rileva il meter (si trova dunque in FallBack)\r\n3) Dall\'app, andando nella pagina \"Export Limitation\" dove è possibile settare i partecipanti al CLC, +NON è possibile rimuovere l\'inverter+. Questo fa si che l\'inverter rimanga permanentemente a potenza nulla.\r\n\r\nParlando con Tacchini e Faggiani ho appreso che:\r\n- Con inverter acceso, l\'interruzione di comunicazione del meter viene rilevata dal Q1 e mostrata sull\'app con il meter ancora presente come partecipante all\'export limitation ma non comunicante (\"x\" rossa). In questa condizione è possibile agire sul meter stesso o sugli inverter rimuovendoli dall\'export limitation. \r\n- Partendo da questa condizione, se si esegue un reset della logica dell\'inverter (es. notte/giorno), nel campo meter non compare più il dispositivo e viene indicato \"meter assente\".\r\n\r\nQuesta implementazione è frutto di una specifica richiesta del marketing finalizzata ad inibire la possibilità di modifiche all\'impianto in assenza di comunicazione con il meter. \r\n\r\nDa questo la mia osservazione: se sul campo si verifica quanto sopra, ovvero che il meter perde la connessione o si danneggia irrimediabilemente, l\'indomani, al riavvio della logica, tutti gli inverter rimarranno perennemente a potenza nulla. *Fintanto che il meter non viene sostituito o recupera la connessione NON c\'è modo di modificare questo stato.*\r\n\r\nDal momento che a nostro parere questo è molto limitativo, vorremmo conferma che il comportamento implementato era ciò che si voleva e che il caso d\'uso sopra descritto era stato preso in considerazione.\r\n\r\nAd oggi, a livello di app, sarebbe sufficiente poter rimuovere gli inverter come partecipanti all\'Export Limitation anche in assenza di meter partecipanti e comunicanti all\'export limitation, consentendogli così di produrre energia.',NULL,943,1,NULL,5,NULL,340,9,'2019-12-16 16:25:54','2020-02-21 15:02:33','2019-12-16',0,NULL,14935,14935,44,45,0,NULL,NULL,NULL);
