INSERT INTO `journals` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (53761,14580,'Issue',195,'Eseguito nuovo test nel periodo 4-5Mar con SW Q1:\r\n\"elcFirmwareVersion\": \"0.3.6.4\", \"regulatorVersion\": \"5\".\r\nINV_9 settato al 100% --> in questo modo il carico simulato varia istante dopo istante a causa delle fluttuazioni del derating Pout vs Vin di INV_9\r\nImpostato set-point 11% -1%GB = 10% = 44KW\r\nSample rate a 2 secondi\r\n\r\n*Niente da segnalare. Test OK. Ci sono fluttuazioni ma vengono recuperate entro 2-4secondi.*\r\nCi sono state 3 segnalazioni di errore di risposta delle API su un totale di migliaia di richieste. Tutto OK.\r\nAllego fiel escplicativi.\r\nIn assenza di variazioni di carico --> tutto OK.\r\nNecessario ripetere il test con il SW Q1 aggiornato in presenza di variazioni di carico.','2019-03-05 14:24:33',0);
INSERT INTO `journals` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (53811,14580,'Issue',195,'Eseguito nuovo test nel periodo 6-7Mar con SW Q1:\r\n\"elcFirmwareVersion\": \"0.3.7\", \"regulatorVersion\": \"5\".\r\n•	Potenza nominale impianto = 440KW\r\n•	Potenza effettivamente disponibile causa derating vs Vin = 330KW.\r\n•	Impostato set-point PEL a 0% con gb 1% (Zero power export)\r\n•	Impostato carico in modo tale eseguisse transizioni lineari tra 16.5KW e 231KW con due velocità: 10 e 20secondi.\r\n•	Cambiati i parametri di fallback in accordo a requisiti AUSTRALIA:\r\n                \"fallback\": {\r\n            \"enable\": true,\r\n            \"percentage\": 0,\r\n            \"deadline\": 14000 (msec),\r\n            \"guardTimeS\": 60 (sec).\r\n\r\n\r\n*Niente da segnalare. Test OK. Ci sono fluttuazioni ma vengono recuperate nei tempi previsti dalle impostazioni.*\r\nAllego files esplicativi\r\n\r\nCambio stato su RESOLVED.\r\nAspetto valutazione di Aleesandro per chiudere','2019-03-07 12:02:17',0);
INSERT INTO `journals` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (53934,14580,'Issue',195,'Impostato controllo su METER B24\r\nEseguito nuovo test nel periodo 7-8Mar con SW Q1:\r\n\"elcFirmwareVersion\": \"0.3.7\", \"regulatorVersion\": \"5\".\r\n• Potenza nominale impianto = 440KW\r\n• Potenza effettivamente disponibile causa derating vs Vin = 330KW.\r\n• Impostato set-point PEL a 0% con gb 1% (Zero power export)\r\n• Impostato carico in modo tale eseguisse transizioni lineari tra 16.5KW e 231KW con due velocità: 10 e 20secondi.\r\n• Cambiati i parametri di fallback in accordo a requisiti AUSTRALIA:\r\n\"fallback\": {\r\n\"enable\": true,\r\n\"percentage\": 0,\r\n\"deadline\": 14000 (msec),\r\n\"guardTimeS\": 60 (sec).\r\n\r\n*Niente da segnalare. Test OK. Ci sono fluttuazioni ma vengono recuperate nei tempi previsti dalle impostazioni.*\r\n*Le performance PEL-CLC sono migliori con METER B24.*\r\nL\'unica interruzione temporanea di potenza è causata dal periodic Grid Off degli inverters.\r\nAllego files esplicativi','2019-03-08 12:10:14',0);
INSERT INTO `journals` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (53959,14580,'Issue',195,'Impostato controllo su METER B24\r\nEseguito nuovo test nel periodo 8-11Mar con SW Q1:\r\n\"elcFirmwareVersion\": \"0.3.7.1\", \"regulatorVersion\": \"5\".\r\n• Potenza nominale impianto = 440KW\r\n• Potenza effettivamente disponibile causa derating vs Vin = 330KW.\r\n• Impostato set-point PEL a 18% con gb 1% (74.8KW)\r\n• Impostato carico all 100%.\r\n• Cambiati i parametri di fallback in accordo a requisiti AUSTRALIA:\r\n\"fallback\": {\r\n\"enable\": true,\r\n\"percentage\": 0,\r\n\"deadline\": 14000 (msec),\r\n\"guardTimeS\": 60 (sec).\r\n\r\nNiente da segnalare. Test OK. Ci sono fluttuazioni ma vengono recuperate nei tempi previsti dalle impostazioni.\r\nLe performance PEL-CLC sono migliori con METER B24.\r\nL\'uniche interruzione temporanea di potenza è causata dal periodic Grid Off degli inverters.\r\nAllego files esplicativi','2019-03-11 12:56:21',0);
