INSERT INTO `journals` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (58374,15281,'Issue',125,'Ne ho parlato con Genovesi e non ci è chiaro quale possa essere l\'impatto del problema.\r\n\r\nIl mancato posizionamento della potenza di uscita si può presentare anche in altri contesti al di fuori di Export Limitation, ad esempio con un controllore esterno di potenza?\r\n\r\nQuesto problema può impattare sulla capacità dell\'inverter di soddisfare i 2s della norma spagnola UNE?\r\n\r\nA nostro avviso occorre risolvere il problema se questo impatta sul soddisfacimento delle normative, incluse quelle inerenti al controllo.','2019-10-02 17:34:40',0);
