INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (11126,2,266,'Cost Optimization UNO-DM XX-TL-PLU-US ( Internal Plastic Carter Replacement )','*Description:*\r\n\r\nThe internal plastic carter replacement with a Mylar sheet ensures anyway the proper insulation between some high voltage and low voltage parts (Ethernet vs AC power cables or primary components). \r\nThe current internal plastic carter provides physical protection from incidental contact with internal components (SMD cap, resistor, IC,…) during the installation or maintenance services, furthermore it must ensure the Internal Thermal equalization between the varios components.\r\n\r\n*Benefits:*\r\n\r\nThe internal plastic carter replacement will allow a COST REDUCTION about 2,48€ for unit.\r\n\r\n*What to do:*\r\n\r\nMake an appointment to start the Thermal Test of reliability (Eduardo Zedda) on the product to validate it. \r\n \r\n\r\n\r\n\r\n',NULL,655,5,367,5,NULL,367,3,'2017-12-20 10:40:13','2020-09-23 11:15:02','2017-12-20',100,NULL,NULL,11126,1,2,0,'2020-09-23 11:15:02',NULL,3492);
