INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (11111,2,268,'New kind of pcb integrated heatsink using filled & capped vias with Cu enrichment  (patents possibility)','Idea: \r\n> > New kind of pcb integrated heatsink for heat dissipation in SMD power device. \r\nBenefits:\r\n> > this kind of solution could improve the quality of productive process and reduce the labor cost.\r\nDescription:\r\n> > Use through hole internal vias as path for the heat dissipation of power SMD device. Increase the through hole copper layer is a way to reduce thermal resistence between the top end the bottom of PCB. The capping of vias matrix with Cu layer is a way to reduce trasversal thermal resistence and prevent the solder voiding. In this case each vias is filled with a particular paste (THP-100DX1). *Illustrated solution is widely used and can not be taken into consideration for a patent possibility.*\r\nThis application has high potential for the development of future products.\r\n\r\n\r\n',NULL,660,5,NULL,4,NULL,2,16,'2017-12-18 17:07:10','2018-01-04 08:35:28','2017-12-18',100,NULL,NULL,11111,1,2,0,'2017-12-21 15:17:07',NULL,3477);
