INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (13973,1,270,'Error E077 Korea','In allegato una presentazione della Korea con problemi di E077 (sembra a seguito di aggiornamento FW…. Paolo ha chiesto a Ji-Hun il corretto ordine delle attività che ji-Hun ha fatto per avere il problema). \r\nNella versione 1736B si parla di una patch per evitare la corruzione della EEPROM.\r\n\r\nIn “APPLICATION” firmware Implemented patch to solve EEPROM issue. After a firmware upgrade operation, in a very low percentage of cases (0.1%) the inverter remains latched in status E077 – System configuration Error and it is no more able to export power. This error status is due to the “corruption” of internal EEPROM that result unwritable. This patch introduced on MICRO used in combination with patch introduced on BOOTLOADER avoid the corruption of EE during upload and it is also able to unlatch and to restore normal operation on unit installed on field (it is enaugh to upload only MICROPROCESSOR firmware).\r\n\r\nIn un’altra email, Ji-Hun ha iniziato a sostituire le schede di controllo quando si ha un “Wrong model code” come vedi sotto vi sono. \r\nIn tale email Ji-Hun allegata presentazione mostrando un pezzo che perde i dati di targa.\r\n\r\n\r\n\r\n\r\n',NULL,665,5,22,6,NULL,22,5,'2019-01-04 15:53:57','2020-07-06 15:42:27','2019-01-04',100,NULL,NULL,13973,1,2,0,'2020-02-05 08:02:52',NULL,6294);
