INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (14698,1,270,'Fix per reset delle statistiche','Fissare il problema del reset delle statistiche.\r\nNon appare il W024 quando si richiede il reset delle statiche.\r\n\r\nInoltre in caso delle locazioni \"validation bytes” nella I2CEEPROM (memoria statistiche) non siano lette correttamente, ho un reset delle statistiche e CLOCK NOT SET come messaggio su display (è un pò una scopertura). In questo caso però non vedrei il problema dato che non ho un W024 nei LOG.\r\n\r\nLa lettura delle locazioni \"validation bytes\" è eseguito troppo velocemente (iterazioni ogni ms)\r\n',NULL,665,5,22,4,NULL,22,1,'2019-03-07 10:52:31','2019-03-07 10:53:44','2019-03-07',100,24,NULL,14698,1,2,0,'2019-03-07 10:53:44',NULL,6976);
INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (11142,2,271,'PVI-10/12.5-TL failure 817M0602000-G (SMAJ20A diode)','Issue:\r\n> > Failure of diode SMAJ20A (part n.817M0602000-G).\r\nDescription:\r\n> > This diode is used in a wide range of products: PVI-10/12-TL, PVI-3.0/3.6/4.2-TL, PVI-5000/6000-TL, TRIO-20/27.6-TL, TRIO-5.8/7.5/8.5-TL. The problem is relevant for PVI-10/12-TL family.  817M0602000-G is a clamp diode with the purpouse of protecting 18V auxiliary circuits. The analysis of the data shows that most of the failures are concentrated in the years 2014-2015. The vendor analysis shows that, during that period, there was a supplier change for the 817M0602000-G (file excel attached). This change could be the failure reason different quality level between the suppliers.\r\n\r\n> > Even the transformer can be the cause of the failure. It\'s necessary to proceed with a similar analysis and, if required, check the goodness of the windings of the 18V output.\r\n\r\n> > The supplier analysis shows that:\r\n* failure rate increasing and the supplier change are strictly correlated.\r\n*Conclusions:*\r\n> > This is a quality issue.',NULL,668,5,NULL,4,NULL,2,8,'2017-12-21 16:39:38','2018-01-04 08:34:23','2017-12-21',100,NULL,NULL,11142,1,2,0,'2018-01-04 08:34:23',NULL,3508);
INSERT INTO `` (`id`,`tracker_id`,`project_id`,`subject`,`description`,`due_date`,`category_id`,`status_id`,`assigned_to_id`,`priority_id`,`fixed_version_id`,`author_id`,`lock_version`,`created_on`,`updated_on`,`start_date`,`done_ratio`,`estimated_hours`,`parent_id`,`root_id`,`lft`,`rgt`,`is_private`,`closed_on`,`sprint_id`,`position`) VALUES (11615,2,271,'Glued relays due to OVRT inrush current','Service is tryng to draw up ROS procedure for PVI-12.5-TL-OUTD.\r\nFor \"nazionale\" version ROS procedure contains the Three-Phase bridge upgrade. This upgrede is already used in \"STAGE1\" and \"STAGE2\" versions. The bridge introduction save the inverter from inrush current due to OVRT. The collateral effect is that the inrush current passing through the bridge cause the solding of the output relays.\r\nWe want to reduce this problem.\r\nThe idea is to use a resistor limiter in series between the bridge and the bulk.\r\n\r\nfrom the tests conducted, the application of the resistance is beneficial for the machine but there is no concrete evidence about the resolution of the problem of bonding the relays.\r\n\r\n\r\n\r\n ',NULL,668,5,NULL,4,NULL,2,15,'2018-02-13 15:44:06','2018-04-10 09:25:59','2018-02-13',100,NULL,NULL,11615,1,2,0,'2018-04-10 09:25:59',NULL,3960);
