INSERT INTO `` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (96156,11740,'Issue',241,'A seguito di una prima analisi sulla scheda logica VCA.V506.4 risultano necessarie alcune modifiche per permettere il corretto funzionamento.\r\n\r\nSostituire R178, attualmente 1K, con 10K --> ripristinare la lettura di corrente del CH1\r\nRimuovere R312 , eseguire filatura tra pin 13 di U6 e pin 135 di U6 --> ripristinare la lettura del GND mode (allego screenshot)\r\n!filatura%20GND%20mode.jpg!\r\n','2021-03-24 13:02:30',0);
INSERT INTO `` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (114506,11740,'Issue',49,'Dopo accensione a banco con connessione in rete, l\'unità di laboratorio con nuova scheda logica ZGN.V0506.4 (con filatura e modifiche sopra di Filippo) ha passato il collaudo in ditta esterna Teseo (Opificio, via lungarno, ex PK, ex Consorzio) con esito positivo in data 9/9/2021.\r\nL\'unica mancanza era la possibilità di variare indirizzo dell\'inverter da RS485 (era invece già possibile da display).\r\nQuesta è già stata risolta nel pacchetto che è qui allegato, ed.2136E. 10/9/2021.\r\nAccensione e collaudo sono avvenuti con impostazione su std europeo.\r\n\r\nLe verifiche preliminari a banco sono state:\r\nconnessione in rete senza allarmi per Vin da Regatron a tensione costante\r\nimpostazione country standard (da fare system integration tests?)\r\nverifica funzionalità con scheda WiFi (da ripetere e mettere in Aurora Vision)\r\nverifica funzionalità display\r\n\r\noccorre definire test e timing dei test DVT (Fiesoli, Palumbo, Rossi)\r\noccorre trovare un beta tester in Italia per installare e verificare in campo (Fiesoli)\r\noccorre completare quanto più possibile in laboratorio testing delle protezioni (Riso, Pout, Iin) (Lalli)\r\noccorre condurre accensione a 60Hz (Lalli) \r\n\r\n\r\n','2021-09-10 17:24:44',0);
INSERT INTO `` (`id`,`journalized_id`,`journalized_type`,`user_id`,`notes`,`created_on`,`private_notes`) VALUES (117114,11740,'Issue',49,'done definire test e timing dei test DVT (Fiesoli, Palumbo, Rossi): evitare DVT con campagna beta testing\r\ndone trovare un beta tester in Italia per installare e verificare in campo: a Montemurlo con CSCAutomazioni installato proto di laboratorio (eseguito final test al collaudo in PK) nell\'impianto 023 ISAC, registrato in Aurora Vision.\r\nopen occorre ancora completare in laboratorio testing delle protezioni (Riso, Pout, Iin) (Lalli)\r\nopen occorre condurre accensione a 60Hz (Lalli)','2021-10-08 18:08:02',0);
